INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346031, 'Amitriptyline', 'Dicoumarol', 'Moderate', 'Some reports suggest that tricyclic antidepressants may increase or decrease the anticoagulant effect of warfarin by an unknown mechanism. Similar effects may occur with other oral anticoagulants.', 'DDInter', 1767317475, 'Until more information is available, it may be advisable to monitor the International Normalized Ratio (INR) or prothrombin time and clinical response if these agents are administered concomitantly. Patients should be advised to promptly report possible signs of bleeding (e.g., pain, swelling, headache, dizziness, weakness, nosebleeds, bleeding of gums from brushing, unusual bruising, red or brown urine, or red or black stools) or clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Synergism', 'Until more information is available, it may be advisable to monitor the International Normalized Ratio (INR) or prothrombin time and clinical response if these agents are administered concomitantly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
