INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346167, 'Amitriptyline', 'Labetalol', 'Moderate', 'Limited data suggest that labetalol can increase the plasma concentrations of tricyclic antidepressants. The proposed mechanism is competition for the same metabolic pathway. The manufacturers of labetalol indicate that concomitant use with tricyclic antidepressants may increase the incidence of tremors and hypotensive events. Carvedilol may also interact similarly.', 'DDInter', 1767317475, 'Close monitoring for clinical response and tolerance is recommended whenever labetalol is added to or discontinued from an antidepressant regimen. Patients should be advised to notify their physician if they experience excessive antidepressant effects such as tremor, dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or hypotension. Dose adjustments may be necessary.', 'Metabolism', 'Close monitoring for clinical response and tolerance is recommended whenever labetalol is added to or discontinued from an antidepressant regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
