INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37776, 'Azithromycin', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37832/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37777, 'Azithromycin', 'Lovastatin', 'Moderate', 'Azithromycin has been implicated in a single case of rhabdomyolysis during lovastatin treatment. The mechanism of interaction is unknown. Macrolide antibiotics that inhibit CYP450 3A4 may significantly increase the plasma concentrations of HMG-CoA reductase inhibitors; however, azithromycin is generally believed to have little, if any, effect on this isoenzyme.', NULL, 'Until further data are available, caution is advised if azithromycin is prescribed to patients treated with HMG-CoA reductase inhibitors or red yeast rice (which contains lovastatin).', NULL, 'Until further data are available, caution is advised if azithromycin is prescribed to patients treated with HMG-CoA reductase inhibitors or red yeast rice (which contains lovastatin). Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. The HMG-CoA reductase inhibitor should be discontinued if creatine kinase is markedly elevated or if myopathy is suspected or diagnosed.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37833/', '[1] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.[2] Westphal JF "Macrolide - induced clinically relevant drug interactions with cytochrome P-450 (CYP) 3A4: an update focused on clarithromycin, azithromycin, and dirithromycin." Br J Clin Pharmacol 50 (2000): 285-95[3] Grunden JW, Fisher KA "Lovastatin-induced rhabdomyolysis possibly associated with clarithromycin and azithromycin." Ann Pharmacother 31 (1997): 859-63[4] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[5] Amsden GW, Kuye O, Wei GC "A study of the interaction potential of azithromycin and clarithromycin with atorvastatin in healthy volunteers." J Clin Pharmacol 42 (2002): 444-9[6] "Product Information. Baycol (cerivastatin)." Bayer, West Haven, CT.[7] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[8] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[9] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on serum concentrations of simvastatin, simvastatin acid, and HMG-CoA reductase inhibitors." Clin Pharmacol Ther 64 (1998): 477-83[10] Kantola T, Kivisto KT, Neuvonen PJ "Grapefruit juice greatly increases serum concentrations of lovastatin and lovastatin acid." Clin Pharmacol Ther 63 (1998): 397-402[11] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[12] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[13] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[14] Thompson PD, Clarkson P, Karas RH "Statin-associated myopathy." JAMA 289 (2003): 1681-90[15] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.', 1767363021);
