INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37832, 'Azithromycin', 'Pasireotide', 'Major', 'Pasireotide can cause bradycardia and QT prolongation. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if pasireotide is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if pasireotide is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting pasireotide therapy and periodically during treatment in accordance with the product labeling. Hypokalemia and hypomagnesemia must be corrected prior to pasireotide administration.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37888/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Signifor (pasireotide)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37833, 'Azithromycin', 'Pazopanib', 'Major', 'Coadministration with potent inhibitors of P-glycoprotein (P-gp) or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations and risk of toxicity of pazopanib. The proposed mechanism involves inhibition of P-gp- or BCRP-mediated transporters of pazopanib metabolism. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', NULL, 'Concomitant use of pazopanib with potent inhibitors of P-gp or BCRP should generally be avoided.', NULL, 'Concomitant use of pazopanib with potent inhibitors of P-gp or BCRP should generally be avoided. Selection of an alternate concomitant medication with no or minimal potential to inhibit P-gp or BCRP should be considered. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37889/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Tan AR, Gibbon DG, Stein MN, et al. "Effects of ketoconazole and esomeprazole on the pharmacokinetics of pazopanib in patients with solid tumors." Cancer Chemother Pharmacol 71 (2013): 1635-43[3] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[6] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37834, 'Azithromycin', 'Pegaspargase', 'Moderate', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury. Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults. Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.', NULL, 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins). Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs. Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37890/', '[1] "Product Information. Oncaspar (pegaspargase)." Rhone-Poulenc Rorer, Collegeville, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Bodmer M, Sulz M, Stadlmann S, Droll A, Terracciano L, Krahenbuhl S "Fatal liver failure in an adult patient with acute lymphoblastic leukemia following treatment with L-asparaginase." Digestion 74 (2006): epub[4] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[5] "Product Information. Elspar (asparaginase)." Merck & Co, Inc, West Point, PA.[6] Christ TN, Stock W, Knoebel RW "Incidence of asparaginase-related hepatotoxicity, pancreatitis, and thrombotic events in adults with acute lymphoblastic leukemia treated with a pediatric-inspired regimen." J Oncol Pharm Pract 24 (2018): 299-308[7] Lu G, Karur V, Herrington JD, Walker MG "Successful treatment of pegaspargase-induced acute hepatotoxicity with vitamin B complex and L-carnitine" Proc (Bayl Univ Med Cent) 29 (2016): 46-7[8] Jenkins R, Perlin E "Severe hepatotoxicity from Escherichia coli L-asparaginase." J Natl Med Assoc 79 (1987): 775-9[9] "Product Information. Asparlas (calaspargase pegol)." Servier, Boston, MA.[10] Burke PW, Aldoss I, Lunning MA, et.al "High-grade PEGylated asparaginase-related hepatotoxicity occurrence in a pediatric-inspired adult acute lymphoblastic leukemia regimen does not necessarily predict recurrent hepatotoxicity in subsequent cycles." Blood 122 (2013): 2671[11] "Product Information. Erwinaze (asparaginase Erwinia chrysanthemi)." EUSA Pharma, Newtown, PA.[12] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[13] Al-Nawakil C, Willems L, Mauprivez C, et.al "Successful treatment of l-asparaginase-induced severe acute hepatotoxicity using mitochondrial cofactors." Leuk Lymphoma 55 (2014): 1670-4', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37835, 'Azithromycin', 'Peginterferon beta-1a', 'Moderate', 'Coadministration of beta interferons with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Use of beta interferons has been associated with rare cases of liver injury, including autoimmune hepatitis and severe liver damage leading to hepatic failure, some of which required transplantation.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when beta interferons are used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; other interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37891/', '[1] "Product Information. Plegridy (peginterferon beta-1a)." Biogen Idec Inc, Cambridge, MA.[2] "Product Information. Betaseron (interferon beta-1b)." Berlex, Richmond, CA.[3] "Product Information. Rebif (interferon beta-1a)." Serono Laboratories Inc, Norwell, MA.[4] "Product Information. Avonex (interferon beta-1a)." Biogen, Cambridge, MA.', 1767363021);
