INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37847, 'Azithromycin', 'Piperacillin', 'Minor', 'Although some in vitro data indicate synergism between macrolide antibiotics and penicillins, other in vitro data indicate antagonism. When these drugs are given together, neither has predictable therapeutic efficacy. Data are available for erythromycin, although theoretically this interaction could occur with any macrolide. Except for monitoring of the effectiveness of antibiotic therapy, no special precautions appear to be necessary.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/37903/', '[1] Strom J "Penicillin and erythromycin singly and in combination in scarlatina therapy and the interference between them." Antibiot Chemother 11 (1961): 694-7[2] Cohn JR, Jungkind DL, Baker JS "In vitro antagonism by erythromycin of the bactericidal action of antimicrobial agents against common respiratory pathogens." Antimicrob Agents Chemother 18 (1980): 872-6[3] Penn RL, Ward TT, Steigbigel RT "Effects of erythromycin in combination with penicillin, ampicillin, or gentamicin on the growth of listeria monocytogenes." Antimicrob Agents Chemother 22 (1982): 289-94', 1767363021);
