INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37965, 'Aztreonam', 'Pemetrexed', 'Moderate', 'Coadministration with drugs that are eliminated by active tubular secretion may delay and/or decrease the clearance of pemetrexed. The mechanism is competitive inhibition of the renal excretion of pemetrexed, which is primarily eliminated unchanged via glomerular filtration and active tubular secretion. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', NULL, 'Patients receiving pemetrexed in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving pemetrexed in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary. The potential for increased toxicity of pemetrexed such as bone marrow suppression should be considered. Renal function should be closely monitored during therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38021/', '[1] "Product Information. Alimta (pemetrexed)." Lilly, Eli and Company, Indianapolis, IN.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (37966, 'Typhoid vaccine (live)', 'Aztreonam', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', NULL, 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38022/', '[1] UK government "Typhoid. Available from: URL: https://www.gov.uk/government/uploads/system/uploads/attachment_data/file/148512/Green-Book-Chapter-33-dh_125348.pdf." ([cited 2014]):[2] American Medical Association, Division of Drugs and Toxicology