INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38073, 'Entecavir', 'Bacitracin', 'Moderate', 'Because entecavir is primarily eliminated by renal excretion, coadministration with drugs that affect renal function may alter the plasma concentrations of entecavir and/or the coadministered drug. In patients with renal impairment, the apparent oral clearance of entecavir has been shown to decrease as the creatinine clearance decreases.', NULL, 'Caution is advised if entecavir is prescribed in combination with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if entecavir is prescribed in combination with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during therapy with entecavir.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38129/', '[1] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38074, 'Etidronic acid', 'Bacitracin', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38130/', '[1] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Kanis JA, Preston CJ, Yates AJ, Percival RC, Mundy KI, Russell RG "Effects of intravenous diphosphonates on renal