INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38120, 'Zoledronic acid', 'Bacitracin', 'Moderate', 'Coadministration of bisphosphonates with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of bisphosphonates has been associated with nephrotoxicity manifested as deterioration of renal function and renal failure.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised if pamidronate, zoledronic acid, or other intravenous formulations of bisphosphonates must be used in patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides, polypeptide and polymyxin antibiotics, vancomycin, amphotericin B, adefovir, cidofovir, tenofovir, foscarnet, cisplatin, gallium nitrate, lithium, mesalamine, certain immunosuppressants, intravenous pentamidine, high intravenous dosages of methotrexate, high dosages of nonsteroidal anti-inflammatory agents). Renal function and serum electrolytes should be monitored. Patients should have serum creatinine assessed prior to each treatment, and treatment should be withheld for renal deterioration.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38176/', '[1] "Product Information. Zometa (zoledronic acid)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Kanis JA, Preston CJ, Yates AJ, Percival RC, Mundy KI, Russell RG "Effects of intravenous diphosphonates on renal function." Lancet 1 (1983): 1328[3] Chang JT, Green L, Beitz J "Renal failure with the use of zoledronic acid." N Engl J Med 349 (2003): 1676-9; discussion 1676-9[4] "Product Information. Aredia (pamidronate)." Novartis Pharmaceuticals, East Hanover, NJ.[5] Lockridge L, Papac RJ, Perazella MA "Pamidronate-associated nephrotoxicity in a patient with Langerhans''s histiocytosis." Am J Kidney Dis 40 (2002): E2[6] Osullivan TL, Akbari A, Cadnapaphornchai P "Acute renal failure associated with the administration of parenteral etidronate." Ren Fail 16 (1994): 767-73[7] Zazgornik J, Grafinger P, Biesenbach G, Hubmann R, Fridrik M "Acute renal failure and alendronate." Nephrol Dial Transplant 12 (1997): 2797-8[8] Banerjee D, Asif A, Striker L, Preston RA, Bourgoignie JJ, Roth D "Short-term, high-dose pamidronate-induced acute tubular necrosis: The postulated mechanisms of bisphosphonate nephrotoxicity." Am J Kidney Dis 41 (2003): E18[9] Markowitz GS, Fine PL, Stack JI, et al. "Toxic acute tubular necrosis following treatment with zoledronate (Zometa)." Kidney Int 64 (2003): 281-289[10] "Product Information. Boniva (ibandronate)." Roche Laboratories, Nutley, NJ.[11] "Product Information. Ostac (clodronate)." Hoffmann-La Roche Limited, Mississauga, IA.[12] Janssen Van Doorn K, Neyns B, Van Der Niepen P, Verbeelen D "Pamidronate-related nephrotoxicity (tubulointerstitial nephritis) in a patient with osteolytic bone metastases." Nephron 89 (2001): 467-8[13] "Product Information. Didronel Tablets (etidronate)." Procter and Gamble Pharmaceuticals, Cincinnati, OH.[14] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38121, 'Amikacin (liposome)', 'Neomycin (topical)', 'Moderate', 'Topically applied aminoglycosides may be systemically absorbed and have potentially additive effects with systemically administered aminoglycosides. The risk of oto- and nephrotoxicity and other adverse effects of aminoglycosides may be increased.', NULL, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.', NULL, 'Topical preparations containing an aminoglycoside should not be used with other aminoglycosides in circumstances where significant systemic absorption could occur (e.g., application to burnt or broken skin; use of large quantities or on large areas for prolonged periods). The normally recommended dosages of these products should not be exceeded.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38177/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38122, 'Atracurium', 'Neomycin (topical)', 'Moderate', 'Aminoglycosides possess neuromuscular blocking activity, which may be additive with that of depolarizing and nondepolarizing muscle relaxants, potentially resulting in severe and/or prolonged respiratory depression during concomitant use. Aminoglycosides inhibit the release of acetylcholine at neuromuscular junctions by interfering with calcium influx. They also appear to decrease the sensitivity of postsynaptic membrane to acetylcholine. The interaction has been observed with various routes of aminoglycoside administration including oral, intramuscular, intravenous, intraperitoneal, esophageal, intraluminal, intradural, and beneath skin flaps. Respiratory paralysis and fatalities have been reported. Patients at increased risk include those with renal impairment and/or hypocalcemia.', NULL, 'Topically applied aminoglycosides may be systemically absorbed, and significant absorption could occur in certain circumstances (e.', NULL, 'Topically applied aminoglycosides may be systemically absorbed, and significant absorption could occur in certain circumstances (e.g., application to burnt or broken skin; use of large quantities or on large areas for prolonged periods). Clinicians should be aware of the potential for interaction if neuromuscular blockers are used in patients who have been treated extensively with topical aminoglycosides. Vital signs should be closely monitored, and ventilatory support should be readily available in case of respiratory arrest. Treatment with anticholinesterases and calcium may not always be effective in reversing the neuromuscular blockade caused by these agents.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38178/', '[1] Warner WA, Sanders E "Neuromuscular blockade associated with gentamicin therapy." JAMA 215 (1971): 1153-4[2] Geha DG, Blitt CD, Moon BJ "Prolonged neuromuscular blockade with pancuronium in the presence of acute renal failure: a case report." Anesth Analg 55 (1976): 343-5[3] Regan AG, Perumbetti PP "Pancuronium and gentamicin interaction in patients with renal failure." Anesth Analg 59 (1980): 393[4] Levanen J, Nordman R "Complete respiratory paralysis caused by a large dose of streptomycin and its treatment with calcium chloride." Ann Clin Res 7 (1975): 47-9[5] Dupuis JY, Martin R, Tetrault JP "Atracurium and vecuronium interaction with gentamicin and tobramycin." Can J Anaesth 36 (1989): 407-11[6] Giala M, Sareyiannis C, Cortsaris N, Paradelis A, Lappas DG "Possible interaction of pancuronium and tubocurarine with oral neomycin." Anaesthesia 37 (1982): 776[7] Jedeikin R, Dolgunski E, Kaplan R, Hoffman S "Prolongation of neuromuscular blocking effect of vecuronium by antibiotics." Anaesthesia 42 (1987): 858-60[8] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[9] Lippmann M, Yang E, Au E, Lee C "Neuromuscular blocking effects of tobramycin, gentamicin, and cefazolin." Anesth Analg 61 (1982): 767-70[10] Chapple DJ, Clark JL, Hughes R "Interaction between atracurium and drugs used in anaesthesia." Br J Anaesth 55 Suppl (1983): s17-22[11] Giala MM, Paradelis AG "Two cases of prolonged respiratory depression due to interaction of pancuronium with colistin and streptomycin." J Antimicrob Chemother 5 (1979): 234-5[12] Burkett L, Bikhazi GB, Thomas KC Jr, Rosenthal DA, Wirta MG, Foldes FF "Mutual potentiation of the neuromuscular effects of antibiotics and relaxants." Anesth Analg 58 (1979): 107-15[13] Ha