INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346565, 'Amlodipine', 'Cisatracurium', 'Moderate', 'Some calcium channel blockers may enhance the neuromuscular blocking properties of nondepolarizing muscle relaxants. The mechanism is not known, but may involve blockage of calcium channels on skeletal muscle. Data are available for verapamil, diltiazem, nicardipine, atracurium, vecuronium, and pancuronium.', 'DDInter', 1767317475, 'Patients should be closely monitored for prolonged neuromuscular blockade. Reduced doses of muscle relaxants may be necessary. Profound neuromuscular blockade may be reversed by neostigmine or edrophonium.', 'Synergism', 'Patients should be closely monitored for prolonged neuromuscular blockade.', NULL, NULL, 'Moderate', 'pharmacodynamic');
