INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346749, 'Amlodipine', 'Pancuronium', 'Moderate', 'Some calcium channel blockers may enhance the neuromuscular blocking properties of nondepolarizing muscle relaxants. The mechanism is not known, but may involve blockage of calcium channels on skeletal muscle. Data are available for verapamil, diltiazem, nicardipine, atracurium, vecuronium, and pancuronium.', 'DDInter', 1767317475, 'Patients should be closely monitored for prolonged neuromuscular blockade. Reduced doses of muscle relaxants may be necessary. Profound neuromuscular blockade may be reversed by neostigmine or edrophonium.', 'Synergism', 'Patients should be closely monitored for prolonged neuromuscular blockade.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346750, 'Amlodipine', 'Pasireotide', 'Moderate', 'The risk of significant bradycardia and atrioventricular (AV) block may be increased during concomitant treatment of pasireotide with other drugs that slow heart rate or AV conduction such as beta-blockers, calcium channel blockers, atazanavir, digitalis, flecainide, lacosamide, mefloquine, moricizine, and propafenone.', 'DDInter', 1767317475, 'Pasireotide has not been studied in patients receiving other bradycardic drugs. Because bradycardia and AV block are recognized risk factors for torsade de pointes arrhythmia, electrocardiographic monitoring is recommended prior to initiation of pasireotide and during treatment. Dose adjustments of beta blockers and calcium channel blockers or correction of electrolyte disturbances may be necessary.', 'Synergism', 'Pasireotide has not been studied in patients receiving other bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346751, 'Amlodipine', 'Pemigatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro. Increased exposure to pemigatinib may increase the incidence and severity of serious adverse reactions such as abdominal pain, acute kidney injury, cholangitis, cholangitis infective, diarrhea, failure to thrive, hypercalcemia, hyponatremia, pleural effusion, pyrexia, small intestinal obstruction, and urinary tract infection.', 'DDInter', 1767317475, 'Caution is advised if pemigatinib is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance.', 'Metabolism', 'Caution is advised if pemigatinib is coadministered with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
