INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346789, 'Amlodipine', 'Rifapentine', 'Major', 'Potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of calcium channel blockers, the majority of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided. If coadministration is necessary, pharmacologic response should be monitored more closely following the initiation or discontinuation of the CYP450 3A4 inducer, and the CCB dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346790, 'Amlodipine', 'Rilonacept', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317475, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346791, 'Amlodipine', 'Rilpivirine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', 'Metabolism', 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346792, 'Amlodipine', 'Riociguat', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators. Symptomatic hypotension may occur. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', 'DDInter', 1767317475, 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Synergism', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346793, 'Amlodipine', 'Ripretinib', 'Moderate', 'Coadministration of CYP450 3A4 inhibitors with ripretinib may increase the plasma concentrations and the risk of adverse effects (e.g., palmar-plantar erythrodysesthesia, hypertension, cardiac dysfunction, arthralgia/myalgia) of ripretinib and its active metabolite (DP-5439). The proposed mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', 'DDInter', 1767317475, 'Caution is recommended when ripretinib is used concomitantly with a CYP450 3A4 inhibitor. Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary. Patients should be monitored for development of adverse effects.', 'Metabolism', 'Caution is recommended when ripretinib is used concomitantly with a CYP450 3A4 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
