INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346805, 'Amlodipine', 'Secobarbital', 'Moderate', 'Barbiturates may increase the hepatic metabolism of some dihydropyridine calcium channel blockers by inducing CYP450 3A4 hepatic metabolism. The serum concentrations and effects of these calcium channel blockers may be significantly decreased.', 'DDInter', 1767317475, 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted. An increased dosage of the calcium channel blocker may be necessary.', 'Metabolism', 'If concurrent administration cannot be avoided, the patient should be closely monitored for changes in pharmacologic effects whenever a barbiturate is initiated, discontinued or its dosage adjusted.', NULL, NULL, 'Moderate', 'pharmacodynamic');
