INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346868, 'Amlodipine', 'Verapamil', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of amlodipine, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors. Dosage reduction may be required for amlodipine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346869, 'Amlodipine', 'Verteporfin', 'Minor', 'Drugs that enhance the uptake of verteporfin into the vascular epithelium may increase the therapeutic effect of this drug. If clinically appropriate, the morning dose of calcium channel blocker may be skipped on the day of the procedure to minimize the effect.', 'DDInter', 1767317475, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346870, 'Amlodipine', 'Voriconazole', 'Moderate', 'Coadministration with azole antifungal agents may increase the plasma concentrations of calcium channel blockers (CCBs), especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most CCBs.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with azole antifungal agents. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with azole antifungal agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346871, 'Amlodipine', 'Voxelotor', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of amlodipine, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors. Dosage reduction may be required for amlodipine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346872, 'Amlodipine', 'Zanubrutinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when zanubrutinib is used with CYP450 3A4 inhibitors. Patients should be monitored for increased adverse effects such as rash, diarrhea, constipation, cough, hemorrhage, infection, cytopenias, and atrial fibrillation or flutter, and the zanubrutinib dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when zanubrutinib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346873, 'Amlodipine', 'Zileuton', 'Minor', 'Zileuton is believed to be a weak inhibitor of cytochrome P450 3A4 enzymes, of which calcium channel blockers are a known substrate. Although no studies have been conducted to verify or quantify the interaction, caution is recommended when using this combination until further data is available.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346874, 'Amlodipine', 'Ziprasidone', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317475, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346875, 'Amprenavir', 'Atorvastatin', 'Major', 'Protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of atorvastatin. The mechanism is PI inhibition of CYP450 3A4 metabolism.', 'DDInter', 1767317475, 'The benefits of using atorvastatin in combination with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. A lower dosage of atorvastatin should be considered if concomitant use is required. Atorvastatin labeling recommends that the dosage not exceed 20 mg/day when used in combination with fosamprenavir or with dual protease inhibitor therapy consisting of ritonavir plus darunavir, saquinavir, or fosamprenavir. Use of atorvastatin with the fixed combination of atazanavir-cobicistat is not recommended. The dosage should not exceed 40 mg/day when used with nelfinavir. Fluvastatin, pitavastatin, and pravastatin may be safer alternatives, since they are not metabolized by CYP450 3A4. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The benefits of using atorvastatin in combination with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346876, 'Atorvastatin', 'Atazanavir', 'Major', 'Protease inhibitors (PIs), particularly ritonavir, may significantly increase the plasma concentrations of atorvastatin. The mechanism is PI inhibition of CYP450 3A4 metabolism.', 'DDInter', 1767317475, 'The benefits of using atorvastatin in combination with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. A lower dosage of atorvastatin should be considered if concomitant use is required. Atorvastatin labeling recommends that the dosage not exceed 20 mg/day when used in combination with fosamprenavir or with dual protease inhibitor therapy consisting of ritonavir plus darunavir, saquinavir, or fosamprenavir. Use of atorvastatin with the fixed combination of atazanavir-cobicistat is not recommended. The dosage should not exceed 40 mg/day when used with nelfinavir. Fluvastatin, pitavastatin, and pravastatin may be safer alternatives, since they are not metabolized by CYP450 3A4. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The benefits of using atorvastatin in combination with protease inhibitors should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346877, 'Boceprevir', 'Atorvastatin', 'Major', 'Coadministration with boceprevir may significantly increase the plasma concentrations of atorvastatin. The mechanism is boceprevir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of atorvastatin.', 'DDInter', 1767317475, 'The benefits of using atorvastatin in combination with boceprevir should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. A lower dosage of atorvastatin should be considered if concomitant use is required. The product labeling for boceprevir recommends that atorvastatin dosage not exceed 40 mg daily during coadministration. Alternatively, other HMG-CoA reductase inhibitors that are not metabolized by CYP450 3A4 may be considered, such as fluvastatin, pravastatin, pitavastatin, and rosuvastatin. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The benefits of using atorvastatin in combination with boceprevir should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346878, 'Chloroquine', 'Atorvastatin', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', 'DDInter', 1767317475, 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346879, 'Clarithromycin', 'Atorvastatin', 'Major', 'Some macrolide antibiotics inhibit CYP450 3A4 and may elevate the plasma concentrations of HMG-CoA reductase inhibitors that are metabolized by the isoenzyme. Macrolides that may significantly inhibit CYP450 3A4 include troleandomycin, erythromycin, and clarithromycin.', 'DDInter', 1767317475, 'The benefits of using HMG-CoA reductase inhibitors that are metabolized by CYP450 3A4 in combination with medications that can inhibit the isoenzyme such as clarithromycin and erythromycin should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis. A lower dosage of the HMG-CoA reductase inhibitor should be considered if concomitant use is required. Atorvastatin labeling recommends that the dosage not exceed 20 mg/day when used in combination with clarithromycin. Fluvastatin, pitavastatin, and rosuvastatin may be safer alternatives, since they are not metabolized by CYP450 3A4. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The benefits of using HMG-CoA reductase inhibitors that are metabolized by CYP450 3A4 in combination with medications that can inhibit the isoenzyme such as clarithromycin and erythromycin should be carefully weighed against the potentially increased risk of myopathy including rhabdomyolysis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346880, 'Clofibrate', 'Atorvastatin', 'Major', 'Severe myopathy and rhabdomyolysis have been reported during concomitant use of HMG-CoA reductase inhibitors and fibric acid derivatives, especially gemfibrozil. Gemfibrozil has been reported to significantly increase the plasma concentrations of some HMG-CoA reductase inhibitors and/or their active metabolites, including lovastatin, simvastatin, pravastatin, cerivastatin, and rosuvastatin (but not fluvastatin). High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity.', 'DDInter', 1767317475, 'Concurrent use of fibric acid derivatives and HMG-CoA reductase inhibitors should generally be avoided unless the benefit of further alterations in lipid levels is anticipated to outweigh the potential risks. Addition of fibrates to HMG-CoA reductase inhibitor therapy typically provides little additional reduction in LDL cholesterol, but further reductions of triglycerides and increases in HDL cholesterol may be attained. Lovastatin labeling recommends that the dosage not exceed 20 mg daily when prescribed with gemfibrozil or other fibrates. All patients treated with HMG-CoA reductase inhibitors and/or fibrates should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed. In addition, patients should be closely monitored for hepatotoxicity.', 'Others', 'Concurrent use of fibric acid derivatives and HMG-CoA reductase inhibitors should generally be avoided unless the benefit of further alterations in lipid levels is anticipated to outweigh the potential risks.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346881, 'Colchicine', 'Atorvastatin', 'Major', 'Coadministration of colchicine and HMG-CoA reductase inhibitors may increase the risk of myopathy due to a combination of pharmacodynamic and pharmacokinetic effects. These agents are individually myotoxic and may have additive or synergistic effects when used together. In addition, colchicine and some HMG-CoA reductase inhibitors are substrates of the CYP450 3A4 isoenzyme and P-glycoprotein efflux transporter, thus competitive inhibition may occur resulting in increased drug absorption and decreased excretion.', 'DDInter', 1767317475, 'Extreme caution is advised if colchicine is used in combination with HMG-CoA reductase inhibitors, particularly in the elderly and patients with underlying renal or hepatic impairment. Some experts recommend checking the creatine kinase level a week or two after coadministration of these agents and after any dose increase, although such monitoring does not reliably prevent the occurrence of severe myopathy. Patients should be advised to contact their physician if they experience symptoms of toxicity such as abdominal pain, nausea, vomiting, diarrhea, fatigue, myalgia, asthenia, hyporeflexia, paraesthesia, and numbness. The drugs should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism, Synergism', 'Extreme caution is advised if colchicine is used in combination with HMG-CoA reductase inhibitors, particularly in the elderly and patients with underlying renal or hepatic impairment.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346882, 'Conivaptan', 'Atorvastatin', 'Major', 'Coadministration with conivaptan may significantly increase the plasma concentrations of certain HMG-CoA reductase inhibitors and/or their pharmacologically active metabolites. The mechanism is conivaptan inhibition of CYP450 3A4 metabolism.', 'DDInter', 1767317475, 'In general, lovastatin, red yeast rice (which contains lovastatin), and simvastatin should be avoided in patients treated with conivaptan due to the potential for severe interaction. Atorvastatin may possibly be used with caution, although clinical data are lacking. Patients currently receiving these agents should consider an interruption during administration of conivaptan, and allow an appropriate amount of time (at least one week) following completion of conivaptan therapy before resuming these medications. Conivaptan is not expected to interact with fluvastatin, pravastatin, and rosuvastatin, since these agents are not significantly metabolized by CYP450 3A4.', 'Metabolism', 'In general, lovastatin, red yeast rice (which contains lovastatin), and simvastatin should be avoided in patients treated with conivaptan due to the potential for severe interaction.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346883, 'Cyclosporine', 'Atorvastatin', 'Major', 'Coadministration with cyclosporine may significantly increase the plasma concentrations of some HMG-CoA reductase inhibitors and/or their pharmacologically active metabolites. The proposed mechanism is cyclosporine inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of HMG-CoA reductase inhibitors like atorvastatin, lovastatin, simvastatin. In addition, atorvastatin, its metabolites, and the active beta-hydroxyacid form of simvastatin, simvastatin acid, are substrates of the hepatic uptake transporter, organic anion transporting polypeptide (OATP) 1B1, which is also inhibited by cyclosporine. Use of cyclosporine with atorvastatin or other HMG-CoA reductase inhibitors has resulted in musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', 'DDInter', 1767317475, 'The concomitant use of atorvastatin in combination with cyclosporine should generally be avoided. Fluvastatin or pravastatin may be considered as alternatives in patients receiving cyclosporine as they are not extensively metabolized by CYP450 3A4. However, the benefits of the use of these medicines in combination with cyclosporine should be carefully weighed against the potential risks and dosages titrated with caution. The product labeling for fluvastatin recommends that, when used in combination with cyclosporine, the fluvastatin dose should not exceed 20 mg twice a day when given with cyclosporine. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The concomitant use of atorvastatin in combination with cyclosporine should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346884, 'Danazol', 'Atorvastatin', 'Major', 'The use of danazol with higher dosages of certain HMG-CoA reductase inhibitors may be associated with an increased risk of myopathy. The proposed mechanism is danazol inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of HMG-CoA reductase inhibitors like atorvastatin, lovastatin, simvastatin, as well as some of their pharmacologically active metabolites. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', 'DDInter', 1767317475, 'The benefits of the use of atorvastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) in patients receiving danazol should be carefully weighed against the potential risks. If the combination is prescribed, lower dosages of the HMG-CoA reductase inhibitor should be considered. Fluvastatin, pravastatin, and rosuvastatin are probably safer alternatives in patients receiving danazol, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The benefits of the use of atorvastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) in patients receiving danazol should be carefully weighed against the potential risks.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1346885, 'Darunavir', 'Atorvastatin', 'Major', 'Coadministration with darunavir/ritonavir may significantly increase the plasma concentrations of atorvastatin. The mechanism is inhibition of the CYP450 3A4-mediated metabolism of atorvastatin by darunavir/ritonavir. High levels of statin or HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', 'DDInter', 1767317475, 'Atorvastatin should be initiated at the lowest possible dosage when prescribed with darunavir/ritonavir. The dosage may be increased gradually based on clinical response and tolerance, but should not exceed 20 mg/day. Fluvastatin and rosuvastatin may be safer alternatives in patients treated with protease inhibitors, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Atorvastatin should be initiated at the lowest possible dosage when prescribed with darunavir/ritonavir.', NULL, NULL, 'Major', 'pharmacodynamic');
