INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77017, 'Cyclosporine', 'Olmesartan', 'Moderate', 'Coadministration of cyclosporine with ACE inhibitors or angiotensin II receptor antagonists may increase the risk of hyperkalemia or precipitate acute renal failure. Cyclosporine causes afferent arteriolar vasoconstriction and decreased glomerular filtration. These effects may be significant when combined with ACE inhibitors or angiotensin II receptor antagonists, which block the mechanism that maintains glomerular filtration rate by constricting the efferent arterioles.', NULL, 'Serum potassium levels and renal function should be checked regularly when cyclosporine is used in combination with ACE inhibitors or angiotensin II receptor antagonists.', NULL, 'Serum potassium levels and renal function should be checked regularly when cyclosporine is used in combination with ACE inhibitors or angiotensin II receptor antagonists. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Excretion', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Abatacept, More', 'Nebivolol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77118/', '[1] Murray BM, Venuto RC, Kohli R, Cunningham EE "Enalapril-associated acute renal failure in 