INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77018, 'Cyclosporine', 'Olsalazine', 'Moderate', 'Coadministration of 5-aminosalicylate with other nephrotoxic agents may increase the risk and severity of renal impairment due to additive effects on the kidney. The use of 5-aminosalicylate or its prodrugs has been associated with rare reports of renal impairment including minimal change nephropathy, acute and chronic interstitial nephritis, and renal failure.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.', NULL, 'Caution is advised when 5-aminosalicylate preparations are prescribed to patients who have recently received or are receiving treatment with other potentially nephrotoxic agents (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during 5-aminosalicylate therapy.', 'Synergism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Beclomethasone dipropionate, Cromoglicic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77119/', '[1] "Product Information. Canasa (mesalamine (5-aminosalicylic acid))." Axcan Scandipharm Inc, Birmingham, AL.[2] Schreiber S. Hamling J, Zehnter E, et al "Renal tubular dysfunction in patients with inflammatory bowel disease treated with aminosalicylate." Gut 40 (1997): 761-6[3] "Product Information. Lialda (mesalamine (5-aminosalicylic acid))." Shire US Inc, Florence, KY.[4] Novis BH, Korzets Z, Chen P, Bernheim J "Nephrotic syndrome after treatment with 5-aminosalicylic acid." Br Med J (Clin Res Ed) 296 (1988): 1442', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77019, 'Cyclosporine', 'Omacetaxine mepesuccinate', 'Moderate', 'The use of omacetaxine with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Omacetaxine alone may cause severe myelosuppression, neutropenia, lymphopenia, and opportunistic infections. The risk may theoretically increase when coadministered with other hematotoxic therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, 'Caution is advised if omacetaxine must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms such as fever, chills, shortness of breath, fatigue, and any unusual bleeding or bruising.', 'Synergism', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab, Ascorbic acid, Vitamin A, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Porfimer sodium, Selinexor, Aflibercept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77120/', '[1] "Product Information. Synribo (omacetaxine)." Teva Pharmaceuticals USA, North Wales, PA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77020, 'Cyclosporine', 'Omeprazole', 'Moderate', 'Chronic use of proton pump inhibitors (PPIs) may induce hypomagnesemia, and the risk may be increased during concomitant use of other agents that can cause magnesium loss such as cyclosporine. The mechanism via which hypomagnesemia may occur during long-term PPI use is unknown, although changes in intestinal absorption of magnesium may be involved. Coadministration with omeprazole may alter the blood concentrations of cyclosporine in some patients. The mechanism of interaction is unknown. Both increased and decreased cyclosporine levels have been described in isolated case reports of patients treated concomitantly with omeprazole 40 mg/day.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as cyclosporine.', NULL, 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as cyclosporine. Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients. A potential interaction with omeprazole should also be considered when cyclosporine levels are altered and no other causes can be identified. Occasionally, a dosage adjustment of cyclosporine may be necessary.', 'Absorption', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Abatacept, Upadacitinib, Siponimod, More', 'Vonoprazan, Bismuth subcitrate potassium, Levofloxacin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77121/', '[1] Arranz R, Yanez E, Franceschi JL, Fernandez-Ranada JM "More about omeparazole-cyclosporine interaction." Am J Gastroenterol 88 (1993): 154[2] Castellote E, Bonet J, Lauzurica R, Pastor C, Cofan F, Caralps A "Does interaction between omeprazole and cyclosporin exist?" Nephron 65 (1993): 478[3] Schouler L, Dumas F, Couzigou P, et al "Omeprazole-cyclosporin interaction." Am J Gastroenterol 86 (1991): 1097[4] Blohme I, Idstrom JP, Andersson T "A study of the interaction between omeprazole and cyclosporine in renal transplant patients." Br J Clin Pharmacol 35 (1993): 156-60[5] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Low magnesium levels can be associated with long-term use of proton pump inhibitor drugs (PPIs). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm245011.htm." ([2011 Mar 2]):[6] Ducharme MP, Warbasse LH, Edwards DJ "Disposition of intravenous and oral cyclosporine after administration with grapefruit juice." Clin Pharmacol Ther 57 (1995): 485-91[7] Min DI, Ku YM, Perry PJ, Ukah FO, Ashton K, Martin MF, Hunsicker LG "Effect of grapefruit juice on cyclosporine pharmacokinetics in renal transplant patients." Transplantation 62 (1996): 123-5[8] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[9] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[10] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[11] Honcharik N, Yatscoff RW, Jeffery JR, Rush DN "The effect of meal composition on cyclosporine absorption." Transplantation 52 (1991): 1087-9[12] Tsunoda SM, Harris RZ, Christians U, et al. "Red wine decreases cyclosporine bioavailability." Clin Pharmacol Ther 70 (2001): 462-7[13] Yee GC, Stanley DL, Pessa LJ, et al. "Effect of grrapefruit juice on blood cyclosporin concentration." Lancet 345 (1995): 955-6[14] Ioannidesdemos LL, Christophidis N, Ryan P, Angelis P, Liolios L, Mclean AJ "Dosing implications of a clinical interaction between grapefruit juice and cyclosporine and metabolite concentrations in patients with autoimmune diseases." J Rheumatol 24 (1997): 49-54[15] Oliveira-Freitas VL, Dalla Costa T, Manfro RC, Cruz LB, Schwartsmann G "Influence of purple grape juice in cyclosporine availability." J Ren Nutr 20 (2010): 309-13[16] Tan KKC, Trull AK, Uttridge JA, Metcalfe S, Heyes CS, Facey S, Evans DB "Effect of dietary fat on the pharmacokinetics and pharmacodynamics of cyclosporine in kidney transplant recipients." Clin Pharmacol Ther 57 (1995): 425-33[17] Hollander AAMJ, Vanrooij J, Lentjes EGWM, Arbouw F, Vanbree JB, Schoemaker RC, Vanes LA, Vanderwoude FJ, Cohen AF "The effect of grapefruit juice on cyclosporine and prednisone metabolism in transplant patients." Clin Pharmacol Ther 57 (1995): 318-24[18] Ducharme MP, Provenzano R, Dehoornesmith M, Edwards DJ "Trough concentrations of cyclosporine in blood following administration with grapefruit juice." Br J Clin Pharmacol 36 (1993): 457-9', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77021, 'Cyclosporine', 'Orlistat', 'Major', 'Limited clinical data suggest that orlistat may significantly reduce the oral bioavailability of cyclosporine. By partially inhibiting the absorption of dietary fat, orlistat may indirectly reduce the gastrointestinal absorption of cyclosporine, which is a highly lipid-soluble drug. There have been case reports of patients whose cyclosporine blood levels became subtherapeutic shortly following the initiation of orlistat therapy.', NULL, 'Given the risk of organ rejection associated with inadequate drug levels, orlistat should preferably not be used in transplant patients treated with cyclosporine.', NULL, 'Given the risk of organ rejection associated with inadequate drug levels, orlistat should preferably not be used in transplant patients treated with cyclosporine. If concomitant therapy is necessary, separating the doses by at least 2 hours may help minimize the potential for an interaction. Cyclosporine blood levels should be closely monitored and patients should be monitored for signs of rejection.', 'Absorption', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Tacrolimus, More', 'Dexfenfluramine, Ephedrine, Fenfluramine, Phentermine, Lorcaserin, Sibutramine, Diethylpropion, Mazindol', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77122/', '[1] Colman E, Fossler M "Reduction in blood cyclosporine concentrations by orlistat." N Engl J Med 342 (2000): 1141[2] Nagele H, Petersen B, Bonacker U, Rodiger W "Effect of orlistat on blood cyclosporin concentration in an obese heart transplant patient." Eur J Clin Pharmacol 55 (1999): 667-9[3] Ducharme MP, Warbasse LH, Edwards DJ "Disposition of intravenous and oral cyclosporine after administration with grapefruit juice." Clin Pharmacol Ther 57 (1995): 485-91[4] Min DI, Ku YM, Perry PJ, Ukah FO, Ashton K, Martin MF, Hunsicker LG "Effect of grapefruit juice on cyclosporine pharmacokinetics in renal transplant patients." Transplantation 62 (1996): 123-5[5] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[6] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[7] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[8] Honcharik N, Yatscoff RW, Jeffery JR, Rush DN "The effect of meal composition on cyclosporine absorption." Transplantation 52 (1991): 1087-9[9] Tsunoda SM, Harris RZ, Christians U, et al. "Red wine decreases cyclosporine bioavailability." Clin Pharmacol Ther 70 (2001): 462-7[10] Yee GC, Stanley DL, Pessa LJ, et al. "Effect of grrapefruit juice on blood cyclosporin concentration." Lancet 345 (1995): 955-6[11] Ioannidesdemos LL, Christophidis N, Ryan P, Angelis P, Liolios L, Mclean AJ "Dosing implications of a clinical interaction between grapefruit juice and cyclosporine and metabolite concentrations in patients with autoimmune diseases." J Rheumatol 24 (1997): 49-54[12] Oliveira-Freitas VL, Dalla Costa T, Manfro RC, Cruz LB, Schwartsmann G "Influence of purple grape juice in cyclosporine availability." J Ren Nutr 20 (2010): 309-13[13] Tan KKC, Trull AK, Uttridge JA, Metcalfe S, Heyes CS, Facey S, Evans DB "Effect of dietary fat on the pharmacokinetics and pharmacodynamics of cyclosporine in kidney transplant recipients." Clin Pharmacol Ther 57 (1995): 425-33[14] Hollander AAMJ, Vanrooij J, Lentjes EGWM, Arbouw F, Vanbree JB, Schoemaker RC, Vanes LA, Vanderwoude FJ, Cohen AF "The effect of grapefruit juice on cyclosporine and prednisone metabolism in transplant patients." Clin Pharmacol Ther 57 (1995): 318-24[15] Ducharme MP, Provenzano R, Dehoornesmith M, Edwards DJ "Trough concentrations of cyclosporine in blood following administration with grapefruit juice." Br J Clin Pharmacol 36 (1993): 457-9', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77022, 'Cyclosporine', 'Osilodrostat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of osilodrostat, which is partially metabolized by the isoenzyme.', NULL, 'Caution is advised when osilodrostat is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when osilodrostat is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations for signs and symptoms of hypocortisolism such as nausea, vomiting, abdominal pain, loss of appetite, fatigue, dizziness, hypotension, abnormal electrolyte levels, and hypoglycemia. Decrease dosing or temporarily discontinue osilodrostat if patients experience symptoms of hypocortisolism or if urine free cortisol levels fall below the target range or there is a rapid decrease in cortisol levels. Stop osilodrostat and administer exogenous glucocorticoid replacement therapy if patients have symptoms of adrenal insufficiency and serum or plasma cortisol levels are below target range. Osilodrostat therapy may be restarted at a lower dosage when symptoms have resolved and cortisol values are within target range.', 'Metabolism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77123/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc, Lebanon, NJ.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77023, 'Cyclosporine', 'Osimertinib', 'Moderate', 'Coadministration with osimertinib may alter the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. In vitro, osimertinib has been shown to be a competitive inhibitor as well as inducer of CYP450 3A4.', NULL, 'Concomitant use of osimertinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, 'Concomitant use of osimertinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, More', 'Sunitinib, Regorafenib, Midostaurin, Copanlisib, Lenvatinib, Erlotinib, Abemaciclib, Trametinib, Vandetanib, Dacomitinib, Tivozanib, Alectinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77124/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77024, 'Cyclosporine', 'Oxaliplatin', 'Moderate', 'Theoretically, coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of oxaliplatin, which is primarily eliminated unchanged by the kidney.', NULL, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). The potential for increased toxicity of oxaliplatin such as peripheral sensory neuropathies and neutropenia should be considered. Renal function should be closely monitored during therapy.', 'Excretion', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Porfimer sodium, Selinexor, Aflibercept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77125/', '[1] "Product Information. Eloxatin (oxaliplatin)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 17673694