INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77124, 'Cyclosporine', 'Siltuximab', 'Moderate', 'The use of interleukin blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin inhibitors in patients with chronic inflammatory diseases.', NULL, 'Concomitant use of interleukin blockers with other immuno- or myelosuppressive agents should be avoided if possible.', NULL, 'Concomitant use of interleukin blockers with other immuno- or myelosuppressive agents should be avoided if possible. Caution is advised when interleukin inhibitors are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges such as immunosuppressants or antineoplastic agents. Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of interleukin inhibitor therapy, and the dosage(s) of these drugs adjusted accordingly. Clinicians should note that the effects of interleukin inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Ravulizumab, Mycophenolic acid, Methotrexate, Abatacept, Apremilast, Inebilizumab, Belumosudil, Thalidomide, More', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77225/', '[1] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[2] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc., Horsham, PA, PA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Actemra (tocilizumab)." Genentech, South San Francisco, CA.[5] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.[6] "Product Information. Amevive (alefacept)." Biogen, Cambridge, MA.[7] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company, Indianapolis, IN.[8] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[9] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77125, 'Cyclosporine', 'Simeprevir', 'Major', 'Coadministration of simeprevir with cyclosporine may lead to significantly increased plasma concentrations of simeprevir. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake, P-glycoprotein (P-gp), and CYP450 3A4 by cyclosporine.', NULL, 'Coadministration of cyclosporine and simeprevir should be avoided.', NULL, 'Coadministration of cyclosporine and simeprevir should be avoided.', 'Metabolism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Ascorbic acid, Methazolamide, Oxytetracycline, Trichlormethiazide, Diazoxide, Econazole, Probenecid, Diclofenamide, Mafenide, Tioconazole, Furazolidone, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77226/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[5] Ducharme MP, Warbasse LH, Edwards DJ "Disposition of intravenous and oral cyclosporine after administration with grapefruit juice." Clin Pharmacol Ther 57 (1995): 485-91[6] Min DI, Ku YM, Perry PJ, Ukah FO, Ashton K, Martin MF, Hunsicker LG "Effect of grapefruit juice on cyclosporine pharmacokinetics in renal transplant patients." Transplantation 62 (1996): 123-5[7] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freem