INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77155, 'Cyclosporine', 'Tacrolimus', 'Major', 'Coadministration of tacrolimus and cyclosporine may increase the risk and severity of nephrotoxicity due to additive effects on the kidney. Clinical experience indicates that the combination is associated with increased renal toxicity as evidenced by increased serum creatinine and decreased glomerular filtration rate. In vitro and animal data also suggest that tacrolimus may inhibit the intestinal first-pass metabolism of cyclosporine via CYP450 3A4, resulting in significantly increased bioavailability of the latter.', NULL, 'To avoid undue nephrotoxicity, tacrolimus and cyclosporine should not be used simultaneously.', NULL, 'To avoid undue nephrotoxicity, tacrolimus and cyclosporine should not be used simultaneously. Tacrolimus or cyclosporine should be discontinued for at least 24 hours prior to initiating the other. In the presence of elevated tacrolimus or cyclosporine concentrations, dosing with the other drug usually should be further delayed.', 'Metabolism, Synergism', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Deoxycholic acid, Calcium gluconate, Pimecrolimus, Magnesium sulfate, Glycopyrronium, Aminobenzoic acid, Ivermectin, Caffeine, Cromoglicic acid, Povidone-iodine, Cyclosporine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77256/', '[1] Pichard L, Fabre I, Domergue J, Joyeux H, Maurel P "Effect of FK 506 on human hepatic cytochromes P-450: interaction with CyA (cyclosporine)." Transplant Proc 23 (1991): 2791-3[2] McCaul