INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77156, 'Cyclosporine', 'Tadalafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tadalafil should be considered.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for tadalafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours in patients treated concomitantly with a potent CYP450 3A4 inhibitor, such as erythromycin, itraconazole, ketoconazole, protease inhibitors, and nefazodone. Patients should be advised to promptly notify their physician if they experience potential symptoms of PDE5 inhibitor toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Apomorphine, Pentosan polysulfate, Phenazopyridine, Acetohydroxamic acid, Trospium, Yohimbine, Flavoxate, Avanafil, Papaverine, Magnesium hydroxide, Ammonium chloride, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77257/', '[1] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77157, 'Cyclosporine', 'Talazoparib', 'Moderate', 'Coadministration with inhibitors of breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of this efflux membrane transporter. However, the effect of BCRP inhibitors on the pharmacokinetics of talazoparib has not been studied in vivo.', NULL, 'Caution is recommended when talazoparib is coadministered with inhibitors of BCRP.', NULL, 'Caution is recommended when talazoparib is coadministered with inhibitors of BCRP. Some authorities advise that concomitant use of talazoparib with potent BCRP inhibitors such as cyclosporine should be avoided (UK). Patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Others', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Abatacept, Inebilizumab, Belumosudil, Thalidomide, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Porfimer sodium, Selinexor, Aflibercept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77258/', '[1] "Product Information. Talzenna (talazoparib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77158, 'Cyclosporine', 'Talimogene laherparepvec', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus. Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab, Alteplase, Ascorbic acid, Vitamin A, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Porfimer sodium, Selinexor, Aflibercept, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77259/', '[1] "Product Information. Imlygic (talimogene laherparepvec)." Amgen USA, Thousand Oaks, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77159, 'Cyclosporine', 'Tazemetostat', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress. Data are not available for other, less potent inhibitors.', NULL, 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors.', NULL, 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Porfimer sodium, Selinexor, Aflibercept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77260/', '[1] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[2] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77160, 'Cyclosporine', 'Telaprevir', 'Major', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may significantly increase the plasma concentrations of cyclosporine. The risk of nephro- and neurotoxicity associated with cyclosporine may be increased. The mechanism involves inhibition of CYP450 3A4, the isoenzyme responsible for the intestinal and hepatic metabolism of cyclosporine. Enhanced cyclosporine oral bioavailability due to inhibition of intestinal P-glycoprotein efflux transporter may also contribute.', NULL, 'Caution is advised if cyclosporine is used in combination with boceprevir or telaprevir.', NULL, 'Caution is advised if cyclosporine is used in combination with boceprevir or telaprevir. Cyclosporine blood levels and renal function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of the HCV NS3/4A protease inhibitor. Patients should be advised to notify their physician if they experience possible signs of cyclosporine toxicity such as nausea, vomiting, diarrhea, abdominal pain, dizziness, fatigue, headache, tremors, and convulsions. Some authorities recommend against the use of telaprevir in organ transplant candidates or recipients.', 'Metabolism', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Cabotegravir, Zanamivir, Velpatasvir, Stavudine, Elbasvir, Tecovirimat, Pibrentasvir, Rimantadine, Elvitegravir, Peramivir, Emtricitabine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77261/', '[1] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[4] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[5] Ducharme MP, Warbasse LH, Edwards DJ "Disposition of intravenous and oral cyclosporine after administration with grapefruit juice." Clin Pharmacol Ther 57 (1995): 485-91[6] Min DI, Ku YM, Perry PJ, Ukah FO, Ashton K, Martin MF, Hunsicker LG "Effect of grapefruit juice on cyclosporine pharmacokinetics in renal transplant patients." Transplantation 62 (1996): 123-5[7] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[8] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[9] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[10] Honcharik N, Yatscoff RW, Jeffery JR, Rush DN "The effect of meal composition on cyclosporine absorption." Transplantation 52 (1991): 1087-9[11] Tsunoda SM, Harris RZ, Christians U, et al. "Red wine decreases cyclosporine bioavailability." Clin Pharmacol Ther 70 (2001): 462-7[12] Yee GC, Stanley DL, Pessa LJ, et al. "Effect of grrapefruit juice on blood cyclosporin concentration." Lancet 345 (1995): 955-6[13] Ioannidesdemos LL, Christophidis N, Ryan P, Angelis P, Liolios L, Mclean AJ "Dosing implications of a clinical interaction between grapefruit juice and cyclosporine and metabolite concentrations in patients with autoimmune diseases." J Rheumatol 24 (1997): 49-54[14] Oliveira-Freitas VL, Dalla Costa T, Manfro RC, Cruz LB, Schwartsmann G "Influence of purple grape juice in cyclosporine availability