INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77179, 'Cyclosporine', 'Tofacitinib', 'Major', 'Coadministration of tofacitinib with other immunosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving tofacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids.', NULL, 'Tofacitinib should not be used in combination with other Janus kinase inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.', NULL, 'Tofacitinib should not be used in combination with other Janus kinase inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.g., azathioprine, cyclosporine). Patients receiving tofacitinib should be closely monitored for the development of signs and symptoms of infection during and after treatment, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. If a serious infection, an opportunistic infection, or sepsis develops, tofacitinib should be interrupted until the infection is controlled.', 'Others', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Baricitinib, Tofacitinib, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab, More', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77280/', '[1] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
