INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77319, 'Diatrizoate', 'Procainamide', 'Moderate', 'The use of iodine-containing contrast media for coronary angiography in patients treated with certain antiarrhythmics such as amiodarone may result in significant prolongation of the QT interval. These contrast agents can be arrhythmogenic when injected into the coronary arteries and may have additive effects on cardiac repolarization when coadministered with antiarrhythmic agents that prolong the QT interval.', NULL, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.', NULL, 'Caution is advised if iodine-containing contrast media are used for coronary angiography in patients treated with class IA (e.g., disopyramide, quinidine, procainamide) or class III (e.g., amiodarone, dofetilide, ibutilide, sotalol) antiarrhythmic agents. Increased surveillance and ECG monitoring may be appropriate. Patients who receive outpatient angiographies should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of arrhythmia such as dizziness, palpitations, or syncope.', 'Synergism', 'Ethiodized oil, Iopanoic acid, Tyropanoic acid, Iopodic acid', 'Lidocaine, Dronedarone, Tocainide, Propafenone, Flecainide, Mexiletine, Bretylium, Moricizine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77420/', '[1] Goernig M, Kirmeier T, Krack A, Hartog CS, Figulla HR, Leder U "Iohexol contrast medium induces QT prolongation in amiodarone patients." Br J Clin Pharmacol 58 (2004): 96-98[2] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[3] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77320, 'Diatrizoate', 'Propranolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', 'Acetylsalicylic acid, Ivabradine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77421/', '[1] "Product Information. Osmovist 240 (iotrolan)." Berlex Canada Inc, Lachine, QC.[2] Costa N "Understanding contrast media." J Infus Nurs 27 (2004): 302-12[3] "Product Information. Visipaque (iodixanol)." Nycomed Inc, Princeton, NJ.[4] "Product Information. Ultravist (iopromide)." Berlex, Richmond, CA.', 1767369485);
