INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77789, 'Dabrafenib', 'Voxelotor', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations and pharmacologic effects of voxelotor. The proposed mechanism is accelerated clearance of voxelotor due to induction of the CYP450 3A4 isoenzyme, which is the primary route of elimination of voxelotor.', NULL, 'The use of voxelotor with moderate to potent CYP450 3A4 inducers should generally be avoided.', NULL, 'The use of voxelotor with moderate to potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible. If coadministration is required, the manufacturer recommends increasing the voxelotor dose to 2500 mg once daily. Pharmacologic response to voxelotor should also be monitored more closely.', 'Metabolism', 'Sunitinib, Futibatinib, Regorafenib, Copanlisib, Alpelisib, Vemurafenib, Erlotinib, Lenvatinib, Erdafitinib, Vandetanib, Trametinib, More', 'Hemin, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77890/', '[1] "Product Information. Oxbryta (voxelotor)." Global Blood Therapeutics, Inc., South San Francisco, CA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[4] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs -