INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (38971, 'Dexamethasone', 'Boceprevir', 'Moderate', 'Coadministration with dexamethasone may decrease the plasma concentrations of the hepatitis C virus NS3/4A protease inhibitors, boceprevir and telaprevir. The proposed mechanism is dexamethasone induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of these and other protease inhibitors. The interaction has not been specifically studied with dexamethasone, but has been reported with the potent CYP450 3A4 inducer, rifampin.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir or telaprevir with dexamethasone should be avoided if possible.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of boceprevir or telaprevir with dexamethasone should be avoided if possible. Otherwise, close clinical monitoring of the virologic response is advised. In addition, patients may experience increased systemic glucocorticoid effects and should be monitored for signs and symptoms of hypercorticism such as acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, and bone loss. Dosage reduction for dexamethasone may be required.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39027/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[3] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767363021);
