INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347172, 'Disulfiram', 'Celecoxib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of celecoxib, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of celecoxib should be considered.', 'DDInter', 1767317475, 'Clinical monitoring for altered effects of celecoxib is recommended following initiation or discontinuation of a CYP450 2C9 inhibitor. Dosage adjustments may be required if an interaction is suspected. Patients should be advised to notify their physician if they experience potential signs and symptoms of celecoxib toxicity such as abdominal pain, black or tarry stools, nausea, vomiting, lethargy, fluid retention, swelling of legs, and worsening heart failure.', 'Metabolism', 'Clinical monitoring for altered effects of celecoxib is recommended following initiation or discontinuation of a CYP450 2C9 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
