INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347290, 'Celecoxib', 'Streptomycin', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', 'DDInter', 1767317475, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347291, 'Celecoxib', 'Sulfadiazine', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of celecoxib, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of celecoxib should be considered.', 'DDInter', 1767317475, 'Clinical monitoring for altered effects of celecoxib is recommended following initiation or discontinuation of a CYP450 2C9 inhibitor. Dosage adjustments may be required if an interaction is suspected. Patients should be advised to notify their physician if they experience potential signs and symptoms of celecoxib toxicity such as abdominal pain, black or tarry stools, nausea, vomiting, lethargy, fluid retention, swelling of legs, and worsening heart failure.', 'Metabolism', 'Clinical monitoring for altered effects of celecoxib is recommended following initiation or discontinuation of a CYP450 2C9 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
