INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347345, 'Colesevelam', 'Olmesartan', 'Moderate', 'INTERVAL: Colesevelam may decrease the gastrointestinal absorption of coadministered drugs. Approximately 20% to 40% reductions in systemic exposure (AUC) and 10% to 45% reductions in peak plasma concentration (Cmax) have been reported for cyclosporine, ethinyl estradiol, glimepiride, glyburide, levothyroxine, olmesartan, and sustained-release verapamil during coadministration with colesevelam.', 'DDInter', 1767317475, 'In general, drugs with a narrow therapeutic index as well as those that have been shown to interact with colesevelam should be administered at least 4 hours before the colesevelam dose. Clinical and laboratory monitoring of drug levels and effects should be considered.', 'Absorption', 'In general, drugs with a narrow therapeutic index as well as those that have been shown to interact with colesevelam should be administered at least 4 hours before the colesevelam dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347346, 'Dalteparin', 'Olmesartan', 'Moderate', 'The concomitant use of angiotensin converting enzyme (ACE) inhibitors or angiotensin receptor blockers (ARBs) and heparin or low molecular weight heparins may increase the risk of hyperkalemia. ACE inhibitors, ARBs, and some heparins individually have been associated with increased potassium levels or hyperkalemia.', 'DDInter', 1767317475, 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin. Serum potassium and renal function should be checked regularly.', 'Synergism', 'Caution is advised if ACE inhibitors or ARBs are used with heparin or low molecular weight heparin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347347, 'Darolutamide', 'Olmesartan', 'Moderate', 'Coadministration with darolutamide may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by darolutamide. Increased adverse reactions of the OATP 1B1 and 1B3 substrates may result.', 'DDInter', 1767317475, 'Caution is advised when darolutamide is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darolutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when darolutamide is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates.', NULL, NULL, 'Moderate', 'pharmacodynamic');
