INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347352, 'Drospirenone', 'Olmesartan', 'Moderate', 'The use of drospirenone may potentiate the risk of hyperkalemia in patients receiving chronic treatment with drugs that can increase serum potassium such as angiotensin converting enzyme (ACE) inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, nonsteroidal anti-inflammatory drugs (NSAIDs), potassium preparations, and miscellaneous agents like cyclosporine, heparin, tacrolimus, and trimethoprim. Drospirenone has antimineralocorticoid activity and may induce hyperkalemia in susceptible patients.', 'DDInter', 1767317475, 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Serum potassium levels should be checked regularly during concomitant therapy with drospirenone and other agents that can increase serum potassium.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347353, 'Eltrombopag', 'Olmesartan', 'Moderate', 'Coadministration with eltrombopag may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and/or breast cancer resistance protein (BCRP) transporters. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by eltrombopag. The interaction has been demonstrated for rosuvastatin, a known substrate of both OATP 1B1 and BCRP.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eltrombopag is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347354, 'Eluxadoline', 'Olmesartan', 'Moderate', 'Coadministration with eluxadoline may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and/or breast cancer resistance protein (BCRP) transporters. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by eluxadoline.', 'DDInter', 1767317475, 'Caution is advised when eluxadoline is used concomitantly with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eluxadoline is added to or withdrawn from therapy.', 'Others', 'Caution is advised when eluxadoline is used concomitantly with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
