INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39008, 'Dexamethasone', 'Vibrio cholerae CVD 103-HgR strain live antigen', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39064/', '[1] "Product Information. Imovax Rabies (rabies vaccine)." sanofi pasteur, Swiftwater, PA.[2] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations. Available from: URL: https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chap" ([2017 Aug]):[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] "Product Information. Havrix (hepatitis A vaccine)." SmithKline Beecham, Philadelphia, PA.[5] "Product Information. Fluzone Influenza Vaccine (influenza virus vaccine)." Connaught, Swiftwater, PA.[6] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence. Available from: URL: https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf." ([2017, Apr 17]):[7] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc., Lansing, MI.[8] "Product Information. Omnihib (haemophilus b conjugate vaccine)." SmithKline Beecham, Philadelphia, PA.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39009, 'Dexamethasone', 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'Moderate', 'There are no data regarding the use of live, attenuated cholera vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', NULL, 'The safety and efficacy of live, attenuated cholera vaccine have not been established in patients receiving immunosuppressive therapies.', NULL, 'The safety and efficacy of live, attenuated cholera vaccine have not been established in patients receiving immunosuppressive therapies.', 'Others', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39065/', '[1] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.[3] "Product Information. Vaxchora (cholera vaccine, live)." PaxVax, Miami, FL.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39010, 'Dexamethasone', 'Cholestyramine', 'Moderate', 'INTERVAL: Concurrent administration of bile acid sequestrants may delay and/or decrease the absorption of oral corticosteroids. Bile acid sequestrants can bind certain drugs in the intestine to form an insoluble complex that is excreted in the feces. Reduced bioavailability of hydrocortisone has been reported during concomitant administration with cholestyramine and colestipol. It is not known whether the interaction occurs with other bile acid binding resins such as colesevelam or with corticosteroids other than hydrocortisone, but the possibility should be considered.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', NULL, 'Oral corticosteroids should be administered at least 1 hour before or 4 to 6 hours after a cholestyramine or colestipol dose, and at least 4 hours before a colesevelam dose.', 'Absorption', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Ipratropium, Cromoglicic acid, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Lomitapide, Alirocumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39066/', '[1] Johansson C, Adamsson U, Stierner U, Lindsten T "Interaction by cholestyramine on the uptake of hydrocortisone in the gastrointestinal tract." Acta Med Scand 204 (1978): 509-12[2] "Product Information. Colestid (colestipol HCI)." Pharmacia and Upjohn, Kalamazoo, MI.[3] Nekl KE, Aron DC "Hydrocortisone-colestipol interaction." Ann Pharmacother 27 (1993): 980-1[4] "Product Information. Questran (cholestyramine)." Par Pharmaceutical Inc, Chestnut Ridge, NY.[5] "Product Information. Welchol (colesevelam)" Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39011, 'Dexamethasone', 'Choline salicylate', 'Moderate', 'Coadministration with corticosteroids may decrease the serum concentrations and therapeutic effects of salicylates. Likewise, serum salicylate levels may increase following withdrawal of corticosteroid therapy, potentially resulting in salicylate toxicity. Pharmacologically, the potential for increased gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration and perforation, should be considered due to additive ulcerogenic effects of these agents (especially aspirin) on the GI mucosa.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs. Pharmacologic response to these agents should be monitored more closely whenever a corticosteroid is added to or withdrawn from therapy in patients stabilized on their existing salicylate regimen, and the salicylate dosage adjusted as necessary. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be appropriate, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Excretion', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 'Ziconotide, Methoxyflurane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39067/', '[1] Piper JM, Ray WA, Daugherty JR, Griffin MR "Corticosteroid use and peptic ulcer disease: role of nonsteroidal ani-inflammatory drugs." Ann Intern Med 114 (1991): 735-40[2] Hansen RA, Tu W, Wang J, Ambuehl R, McDonald CJ, Murray MD "Risk of adverse gastrointestinal events from inhaled corticosteroids." Pharmacotherapy 28 (2008): 1325-34[3] Koren G, Roifman C, Gelfand E, Lavi S, Suria D, Stein L "Corticosteroids-salicylate interaction in a case of juvenile rheumatoid arthritis." Ther Drug Monit 9 (1987): 177-9[4] Edelman J, Potter JM, Hackett LP "The effect of intra-articular steroids on plasma salicylate concentrations." Br J Clin Pharmacol 21 (1986): 301-7[5] Baer PA, Shore A, Ikeman RL "Transient fall in serum salicylate levels following intraarticular injection of steroid in patients with rheumatoid arthritis." Arthritis Rheum 30 (1987): 345-7', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39012, 'Dexamethasone', 'Cinoxacin', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', 'Ciclesonide, Mupirocin, Mometasone furoate, Budesonide, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39068/', '[1] Khaliq Y, Zhanel GG "Fluoroquinolone-Associated Tendinopathy: A Critical Review of the Literature." Clin Infect Dis 36 (2003): 1404-1410[2] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[3] van der Linden PD, Sturkenboom MC, Herings RM, Leufkens HM, Rowlands S, Stricker BH "Increased risk of achilles tendon rupture with quinolone antibacterial use, especially in elderly patients taking oral corticosteroids." Arch Intern Med 163 (2003): 1801-7[4] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[5] "Product Information. Baxdela (delafloxacin)." Melinta Therapeutics, Lincolnshire, IL.[6] FDA. U.S. Food and Drug Administration "Information for Healthcare Professionals. Fluoroquinolone Antimicrobial Drugs. FDA Alert [7/8/2008]. Available from: URL: http://www.fda.gov/cder/drug/InfoSheets/HCP/fluoroquinolonesHCP.htm." ([7/8/2008]):[7] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.', 1767369485);
