INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39147, 'Guanfacine', 'Dexamethasone', 'Major', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of guanfacine, which is primarily metabolized by the isoenzyme.', NULL, 'Close monitoring and dosage adjustment should be considered when guanfacine is administered with potent and moderate CYP450 3A4 inducers.', NULL, 'Close monitoring and dosage adjustment should be considered when guanfacine is administered with potent and moderate CYP450 3A4 inducers. For extended-release guanfacine, some manufacturers recommend increasing the dosage up to double the recommended level when initiating therapy in patients who are already receiving a CYP450 3A4 inducer. Further dosage adjustments may be required based on patient tolerance and response. If a CYP450 3A4 inducer is added to existing guanfacine therapy, the guanfacine dosage may be increased up to double the recommended level over 1 to 2 weeks. The dosage should be decreased to the recommended level over 1 to 2 weeks following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39203/', '[1] Kiechel JR, Lavene D, Guerret M, Comoy E, Godin M, Fillastre JP "Pharmacokinetic aspects of guanfacine withdrawal syndrome in a hypertensive patient with chronic renal failure." Eur J Clin Pharmacol 25 (1983): 463-6[2] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Tenex (guanfacine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] "Product Information. Intuniv (guanfacine)." Shire US Inc, Florence, KY.[7] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39148, 'Hepatitis A Vaccine', 'Dexamethasone', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitam