INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39209, 'Dexamethasone', 'Levobupivacaine', 'Minor', 'Coadministration with inducers of CYP450 1A2 and/or 3A4 may decrease the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', NULL, '-', NULL, '-', 'Metabolism', 'Ropivacaine, Chloroprocaine, Tetracaine, Mepivacaine, Procaine, Prilocaine, Articaine, Etidocaine', 'Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Ipratropium, Cromoglicic acid, Prednisolone, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39265/', '[1] "Product Information. Chirocaine (levobupivacaine)" Organon, West Orange, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39210, 'Dexamethasone', 'Levofloxacin', 'Major', 'Concomitant administration of corticosteroids may potentiate the risk of tendinitis and tendon rupture associated with fluoroquinolone treatment. The mechanism is unknown. Tendinitis and tendon rupture have most frequently involved the Achilles tendon, although cases involving the rotator cuff (the shoulder), the hand, the biceps, and the thumb have also been reported. Some have required surgical repair or resulted in prolonged disability. Tendon rupture can occur during or up to several months after completion of fluoroquinolone therapy.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.', NULL, 'Caution is recommended if fluoroquinolones are prescribed in combination with corticosteroids, particularly in patients with other concomitant risk factors (e.g., age over 60 years; recipient of kidney, heart, and/or lung transplant). Patients should be advised to stop taking the fluoroquinolone, avoid exercise and use of the affected area, and promptly contact their physician if they experience pain, swelling, or inflammation of a tendon.', 'Synergism', 'Tinidazole, Bismuth subcitrate potassium, Dexlansoprazole, Esomeprazole, Tinidazole, Cefixime, Secnidazole, Rifamycin, Kanamycin, Netilmicin, Polymyxin B, More', 'Ciclesonide, Mupirocin, Mometasone furoate, Budesonide, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39266/', '[1] Khaliq Y, Zhanel GG "Fluoroquinolone-Associated Tendinopathy: A Critical Review of the Literature." Clin Infect Dis 36 (2003): 1404-1410[2] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[3] van der Linden PD, Sturkenboom MC, Herings RM, Leufkens HM, Rowlands S, Stricker BH "Increased risk of achilles tendon rupture with quinolone antibacterial use, especially in elderly patients taking oral corticosteroids." Arch Intern Med 163 (2003): 1801-7[4] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[5] "Product Information. Baxdela (delafloxacin)." Melinta Therapeutics, Lincolnshire, IL.[6] FDA. U.S. Food and Drug Administration "Information for Healthcare Professionals. Fluoroquinolone Antimicrobial Drugs. FDA Alert [7/8/2008]. Available from: URL: http://www.fda.gov/cder/drug/InfoSheets/HCP/fluoroquinolonesHCP.htm." ([7/8/2008]):[7] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39211, 'Dexamethasone', 'Levacetylmethadol', 'Major', 'Coadministration with inducers of CYP450 3A4 may increase the peak activity and/or decrease the duration of action of levomethadyl acetate, which is primarily metabolized by the isoenzyme. Induction of CYP450 3A4 also results in increased formation of the active metabolites, nor-LAAM and dinor-LAAM. High plasma levels of these metabolites may increase the risk of QT interval prolongation and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. The dose or dosing interval may require adjustment if an interaction is suspected. A 12-lead ECG should be performed before initiating levomethadyl acetate therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Diamorphine, Acamprosate, Lofexidine, Naltrexone, Disulfiram, Nalmefene', 'Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Ipratropium, Cromoglicic acid, Prednisolone, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39267/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39212, 'Dexamethasone', 'Licorice', 'Moderate', 'Licorice may potentiate the effects of corticosteroids. Licorice use has been associated with hypertension as well as sodium and water retention. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects. Prolonged use of licorice has led to a hypermineralocorticoid (pseudohyperaldosteronism) syndrome characterized by hypertension, hypernatremia, hypokalemia, metabolic alkalosis, renin-angiotensin-aldosterone suppression, and edema. In studies and case reports, licorice toxicity has generally been completely reversible within one to several weeks of licorice discontinuation. However, renin-angiotensin-aldosterone axis may be suppressed for up to several months.', NULL, 'Patients receiving prolonged corticosteroid therapy should avoid or limit the consumption of licorice-containing products.', NULL, 'Patients receiving prolonged corticosteroid therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Synergism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39268/', '[1] Bannister B, Ginsburg R, Shneerson J "Cardiac arrest due to liquorice-induced hypokalaemia." Br Med J 2 (1977): 738-9[2] Richard CL, Jurgens TM "Effects of natural health pro