INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39224, 'Dexamethasone', 'Lyme disease vaccine (recombinant OspA)', 'Moderate', 'The administration of Lyme disease vaccine to immunosuppressed patients may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy.', NULL, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39280/', '[1] "Product Information. Lymerix (Lyme disease vaccine)." SmithKline Beecham, Philadelphia, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39225, 'Dexamethasone', 'Macimorelin', 'Moderate', 'Drugs that directly affect the pituitary secretion of growth hormone (e.g., somatostatin, insulin, glucocorticoids, cyclooxygenase inhibitors such as aspirin and nonsteroidal anti-inflammatory drugs) and drugs that may transiently elevate growth hormone levels (e.g., clonidine, levodopa, insulin) may impact the accuracy of the macimorelin diagnostic test.', NULL, 'Concomitant use of macimorelin with drugs affecting growth hormone levels should generally be avoided.', NULL, 'Concomitant use of macimorelin with drugs affecting growth hormone levels should generally be avoided. A sufficient washout period following discontinuation of these drugs is recommended prior to macimorelin administration.', 'Others', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 'Methacholine, Hexaminolevulinate, Secretin human, Methylene blue, Sermorelin, Sorbitol, Vitamin A, Caffeine, Metyrapone, Histamine, Indocyanine green, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39281/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.[2] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.', 1767369485);
