INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39245, 'Dexamethasone', 'Methotrexate', 'Moderate', 'Although they are often used together in clinical practice, limited data suggest that corticosteroids may increase the risk of methotrexate toxicity. Individual cases of severe and fatal bone marrow suppression and fatal systemic moniliasis have been reported.', NULL, 'Close monitoring for signs and symptoms of bone marrow suppression and nephrotoxicity is advisable during concomitant administration.', NULL, 'Close monitoring for signs and symptoms of bone marrow suppression and nephrotoxicity is advisable during concomitant administration. Patients should be advised to report possible symptoms of toxicity including nausea, vomiting, diarrhea, stomatitis, sore throat, chills, fever, rash, unusual bruising or bleeding, jaundice, dark urine, swelling of the extremities, or shortness of breath to their physician.', 'Synergism', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Abatacept, Inebilizumab, Ozanimod, Belimumab, Emapalumab, Sutimlimab, More', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Flunisolide, Ephedrine, Ipratropium, Hyaluronic acid, Nedocromil, Phenylephrine, Beclomethasone dipropionate, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39301/', '[1] Wolff JE, Hauch H, Kuhl J, Egeler RM, Jurgens H "Dexamethasone increases hepatotoxicity of MTX in children with brain tumors." Anticancer Res 18 (1998): 2895-900[2] Roenigk HH, Fowler-Bergfeld W, Curtis GH "Methotrexate for psoriasis in weekly oral doses." Arch Dermatol 99 (1969): 86-93[3] Lafforgue P, Monjanel-Mouterde S, Durand A, Catalin J