INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39362, 'Dexamethasone', 'Ripretinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and anti-tumor activities of ripretinib and its active metabolite (DP-5439). The proposed mechanism is induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', NULL, 'Until more information is available, caution is advised when ripretinib is used with a CYP450 3A4 inducer.', NULL, 'Until more information is available, caution is advised when ripretinib is used with a CYP450 3A4 inducer. Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the ripretinib dosage adjusted as necessary. Patients should be monitored for loss of anti-tumor activity.', 'Metabolism', 'Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Ipratropium, Cromoglicic acid, Prednisolone, More', 'Lenvatinib, Trametinib, Dacomitinib, Tivozanib, Alectinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39418/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Qinlock (ripretinib)." Deciphera Pharmaceuticals, Waltham, MA.[3] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[4] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[5] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit