INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39375, 'Dexamethasone', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 3A4.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Mitotane, Niraparib, Porfimer sodium, Selinexor, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39431/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39376, 'Dexamethasone', 'Ruxolitinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', NULL, 'No dosage adjustment is recommended when ruxolitinib is coadministered with a CYP450 3A4 inducer.', NULL, 'No dosage adjustment is recommended when ruxolitinib is coadministered with a CYP450 3A4 inducer. However, patients should be closely monitored and the dose titrated based on safety and efficacy.', 'Metabolism', 'Flunisolide, Betamethasone, Phenylephrine, Triamcinolone, Azelastine, Ciclesonide, Mupirocin, Hydrocortisone, Ipratropium, Cromoglicic acid, Prednisolone, More', 'Lenvatinib, Trametinib, Dacomitinib, Tivozanib, Alectinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39432/', '[1] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation, Wilmington, DE.[2] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39377, 'Dexamethasone', 'Salmeterol', 'Minor', 'Although they are often combined in clinical practice, the concomitant use of beta-2 adrenergic agonists and corticosteroids may result in additive hypokalemic effects. Since beta-2 agonists can sometimes cause QT interval prolongation, the development of hypokalemia may potentiate the risk of ventricular arrhythmias including torsade de pointes. However, clinical data are limited, and the potential significance is unknown. Patients who are receiving systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or systemic corticosteroid therapy may be at a greater risk of developing hypokalemia.', NULL, '-', NULL, '-', 'Synergism', 'Mometasone furoate, Umeclidinium, Aclidinium, Mometasone, Glycopyrronium, Ipratropium, Olodaterol, Beclomethasone dipropionate', 'Mupirocin, Mometasone furoate, Vitamin A, Hyaluronic acid, Ipratropium, Nedocromil, Mometasone furoate, Fluorometholone, Minocycline, Vitamin A, Isotretinoin, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39433/', '[1] "Product Information. Foradil (formoterol)" Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39378, 'Dexamethasone', 'Salsalate', 'Moderate', 'Coadministration with corticosteroids may decrease the serum concentrations and therapeutic effects of salicylates. Likewise, serum salicylate levels may increase following withdrawal of corticosteroid therapy, potentially resulting in salicylate toxicity. Pharmacologically, the potential for increased gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration and perforation, should be considered due to additive ulcerogenic effects of these agents (especially aspirin) on the GI mucosa.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs. Pharmacologic response to these agents should be monitored more closely whenever a corticosteroid is added to or withdrawn from therapy in patients stabilized on their existing salicylate regimen, and the salicylate dosage adjusted as necessary. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be appropriate, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Excretion', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluticasone, Vitamin A, Ipratropium, More', 'Ziconotide, Methoxyflurane', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39434/', '[1] Piper JM, Ray WA, Daugherty JR, Griffin MR "Corticosteroid use and peptic ulcer disease: role of nonsteroidal ani-inflammatory drugs." Ann Intern Med 114 (1991): 735-40[2] Hansen RA, Tu W, Wang J, Ambuehl R, McDonald CJ, Murray MD "Risk of adverse gastrointestinal events from inhaled corticosteroids." Pharmacotherapy 28 (2008): 1325-34[3] Koren G, Roifman C, Gelfand E, Lavi S, Suria D, Stein L "Corticosteroids-salicylate interaction in a case of juvenile rheumatoid arthritis." Ther Drug Monit 9 (1987): 177-9[4] Edelman J, Potter JM, Hackett LP "The effect of intra-articular steroids on plasma salicylate concentrations." Br J Clin Pharmacol 21 (1986): 301-7[5] Baer PA, Shore A, Ikeman RL "Transient fall in serum salicylate levels following intraarticular injection of steroid in patients with rheumatoid arthritis." Arthritis Rheum 30 (1987): 345-7', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39379, 'Dexamethasone', 'Saquinavir', 'Moderate', 'Coadministration with protease inhibitors may increase the plasma concentration and pharmacologic effects of dexamethasone. The proposed mechanism is protease inhibitor-mediated inhibition of CYP450 3A4, the isoenzyme involved in the metabolic clearance of dexamethasone. Corticosteroids such as dexamethasone may cause hypokalemia and potentiate the risk of QT and/or PR interval prolongation associated with the use of certain protease inhibitors such as atazanavir, lopinavir-ritonavir, and saquinavir-ritonavir. The risk of torsade de pointes arrhythmia, bradycardia, and heart block may be increased.', NULL, 'Caution is advised if dexamethasone must be used concomitantly with protease inhibitors or cobicistat.', NULL, 'Caution is advised if dexamethasone must be used concomitantly with protease inhibitors or cobicistat. Some authorities advise against concomitant use unless the potential benefit outweighs the risk. Adrenal function should be monitored regularly during chronic use of these agents, and dexamethasone dosage adjusted as necessary. Patients should be monitored for symptoms of hypercorticism (e.g., acne, easy bruising, moon face, edema, hirsutism, buffalo hump, skin striae, glucose intolerance, and irregular menstruations), immunosuppression, and osteoporosis. In addition, it may be appropriate to monitor patients for potentially reduced antiretroviral response following initiation or any dosage increase of dexamethasone. Serum potassium and ECG monitoring should also be considered during coadministration of dexamethasone with certain protease inhibitors in accordance with the product labeling.', 'Metabolism', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Brincidofovir, Remdesivir, Elbasvir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, More', 'Mupirocin, Mometasone furoate, Epinephrine, Vitamin A, Ephedrine, Olopatadine, Ipratropium, Cromoglicic acid, Hyaluronic acid, Nedocromil, Phenylephrine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39435/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[7] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39380, 'Dexamethasone', 'Pfizer-BioNTech Covid-19 Vaccine', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Epinephrine, Flunisolide, Ephedrine, Olopatadine, Fluti