INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347544, 'Amobarbital', 'Butorphanol', 'Major', 'Barbiturates may potentiate the central nervous system (CNS) depressant effects of opioids. Concomitant use may result in profound sedation, respiratory depression, coma, and death. On the other hand, some barbiturates can also induce the hepatic metabolism of opioids that are metabolized by CYP450 3A4 such as butorphanol, fentanyl, hydrocodone, methadone and oxycodone, resulting in enhanced clearance. Reduced analgesic efficacy or withdrawal symptoms may occur in patients maintained on their opioid regimen following the addition of a barbiturate. Conversely, discontinuation of the barbiturate may increase plasma concentrations of the opioid and potentiate the risk of overdose and fatal respiratory depression.', 'DDInter', 1767317475, 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate. Particular caution is advisable when a barbiturate is added to or withdrawn from therapy in patients receiving opioids that are CYP450 3A4 substrates, as there may be an increased risk of withdrawal symptoms (e.g., restlessness, insomnia, sweating, lacrimation, or rhinorrhea) following initiation of the barbiturate and overdose following discontinuation. A dosage adjustment for one or both drugs may be required.', 'Synergism', 'The use of opioids in conjunction with other CNS depressants such as barbiturates should generally be avoided unless alternative treatment options are inadequate.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347545, 'Amobarbital', 'Cabazitaxel', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabazitaxel, which is primarily metabolized by the isoenzyme. However, no formal drug interaction studies have been conducted.', 'DDInter', 1767317475, 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. The extent to which other, less potent CYP450 3A4 inducers may interact with cabazitaxel is unknown. Caution is advised if they are used with cabazitaxel.', 'Metabolism', 'Concomitant use of cabazitaxel with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347546, 'Amobarbital', 'Cabozantinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of cabozantinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of cabozantinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347547, 'Amobarbital', 'Calcifediol', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', 'DDInter', 1767317475, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347548, 'Amobarbital', 'Calcitriol', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', 'DDInter', 1767317475, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347549, 'Amobarbital', 'Canagliflozin', 'Moderate', 'Coadministration with uridine diphosphoglucuronate-glucuronosyltransferase (UGT) inducers such as rifampin, phenytoin, phenobarbital, and ritonavir may decrease area under the curve (AUC) of canagliflozin by up to 51%.', 'DDInter', 1767317475, 'If coadministration with a UGT inducer is required, a higher dose of canagliflozin may be needed based on tolerability, glycemic control, and renal function. Monitor glycemic control and refer to the product labeling for specific guidelines on dosing adjustments.', 'Metabolism', 'If coadministration with a UGT inducer is required, a higher dose of canagliflozin may be needed based on tolerability, glycemic control, and renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
