INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347555, 'Amobarbital', 'Cariprazine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', 'DDInter', 1767317475, 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
