INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347576, 'Amobarbital', 'Clomipramine', 'Moderate', 'Tricyclic antidepressants may counteract the anticonvulsive effects of barbiturates by lowering the seizure threshold. Barbiturates may decrease the serum levels and effects of tricyclic antidepressants by induction of hepatic metabolism.', 'DDInter', 1767317475, 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control. Dose adjustments may be required. Monitoring for clinical evidence of additive toxicity and for clinical and/or laboratory evidence of reduced antidepressant effect is also advisable.', 'Antagonism', 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347577, 'Amobarbital', 'Clonazepam', 'Moderate', 'Barbiturates may increase the plasma clearance of clonazepam and thereby reduce its clinical effectiveness. Phenobarbital is the only barbiturate specifically implicated in this interaction. However, other barbiturates may behave in a similar fashion. Also, clonazepam may increase the effectiveness of barbiturates. A case of respiratory depression and coma has been reported in a patient receiving high doses of both clonazepam and amobarbital.', 'DDInter', 1767317475, 'If these drugs are used together, close observation for altered clonazepam and barbiturate effects is indicated.', 'Metabolism, Synergism', 'If these drugs are used together, close observation for altered clonazepam and barbiturate effects is indicated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
