INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347607, 'Amobarbital', 'Dicoumarol', 'Major', 'Barbiturates reduce the effects of oral anticoagulants by inducing their hepatic metabolism. Anticoagulant dosage requirements may be increased by 30% to 60%. After the barbiturate is discontinued, excessive anticoagulation and bleeding may occur if the anticoagulant dose is not reduced.', 'DDInter', 1767317475, 'Close attention should be paid to alterations in coagulation if a patient must take both drugs and for several weeks after the dose of the barbiturate is changed or discontinued. Alternatively, a benzodiazepine might be used instead of a barbiturate, if appropriate. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'Metabolism', 'Close attention should be paid to alterations in coagulation if a patient must take both drugs and for several weeks after the dose of the barbiturate is changed or discontinued.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347608, 'Amobarbital', 'Digitoxin', 'Moderate', 'Barbiturates may decrease serum digitoxin levels, possibly by increasing hepatic conversion of digitoxin to digoxin. Phenobarbital is the only barbiturate specifically implicated in this interaction. However, other barbiturates may behave in a similar fashion.', 'DDInter', 1767317475, 'Close observation for clinical and laboratory evidence of altered digitoxin effect is indicated if a barbiturate and digitoxin must be used together. Digoxin is not expected to be affected to the same extent.', 'Metabolism', 'Close observation for clinical and laboratory evidence of altered digitoxin effect is indicated if a barbiturate and digitoxin must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347609, 'Amobarbital', 'Dihydroergotamine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ergot alkaloids, which are substrates of the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of ergot alkaloids should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347610, 'Amobarbital', 'Dihydrotachysterol', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', 'DDInter', 1767317475, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347611, 'Amobarbital', 'Diltiazem', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of diltiazem, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered. Diltiazem dose adjustment should be guided by clinical response if concomitant use with a CYP450 3A4 inducer is necessary.', 'Metabolism', 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
