INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347613, 'Amobarbital', 'Disopyramide', 'Moderate', 'Barbiturates may decrease the serum concentration of disopyramide resulting in reduced antiarrhythmic efficacy and increased anticholinergic side effects. The mechanism may be induction of the CYP450 3A4 metabolism of disopyramide. Phenobarbital is the only barbiturate specifically implicated in this interaction. However, other barbiturates are also enzyme inducers and may behave in a similar fashion.', 'DDInter', 1767317475, 'Close observation for clinical and laboratory evidence of altered disopyramide effect is indicated. Patients should be advised to notify their physician if they experience lightheadedness, syncope, palpitations, irregular heartbeats, chest pain, dry mouth, or urinary retention.', 'Metabolism', 'Close observation for clinical and laboratory evidence of altered disopyramide effect is indicated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
