INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347615, 'Amobarbital', 'Docetaxel', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of docetaxel, which is a substrate of the isoenzyme. Rifampin and hyperforin, a component of St. John''s wort, have been shown to significantly induce the in vitro metabolism of docetaxel in human hepatocyte cultures.', 'DDInter', 1767317475, 'Coadministration of docetaxel with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided unless the benefit outweighs the potential risk of docetaxel underexposure. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is required, close monitoring for signs of reduced therapeutic efficacy is advised. Other known inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, etravirine, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with docetaxel is unknown.', 'Metabolism', 'Coadministration of docetaxel with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347616, 'Amobarbital', 'Donepezil', 'Moderate', 'Coadministration with drugs that are inducers of the CYP450 3A4 and/or 2D6 enzymatic pathways may decrease the plasma concentrations of donepezil. The mechanism is accelerated clearance of donepezil due to enhanced CYP450 activities.', 'DDInter', 1767317475, 'While clinical data are lacking, the possibility of a diminished therapeutic response to donepezil should be considered in patients receiving concomitant inducer agents.', 'Metabolism', 'While clinical data are lacking, the possibility of a diminished therapeutic response to donepezil should be considered in patients receiving concomitant inducer agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347617, 'Amobarbital', 'Doravirine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of doravirine should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of doravirine should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347618, 'Amobarbital', 'Doxepin', 'Moderate', 'Tricyclic antidepressants may counteract the anticonvulsive effects of barbiturates by lowering the seizure threshold. Barbiturates may decrease the serum levels and effects of tricyclic antidepressants by induction of hepatic metabolism.', 'DDInter', 1767317475, 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control. Dose adjustments may be required. Monitoring for clinical evidence of additive toxicity and for clinical and/or laboratory evidence of reduced antidepressant effect is also advisable.', 'Antagonism', 'If the barbiturate is being taken for a seizure disorder, patients should be closely monitored for loss of seizure control.', NULL, NULL, 'Moderate', 'pharmacodynamic');
