INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347671, 'Guanfacine', 'Amobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of guanfacine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of diminished therapeutic response to guanfacine should be considered during coadministration with CYP450 3A4 inducers. Pharmacologic response to guanfacine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of diminished therapeutic response to guanfacine should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
