INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39508, 'Conjugated estrogens', 'Carfilzomib', 'Major', 'Venous thromboembolic events, including deep vein thrombosis (DVT) and pulmonary embolism (PE), have occurred during treatment with carfilzomib, and concurrent use of other agents also associated with this adverse effect can potentiate the risk.', NULL, 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis.', NULL, 'Caution is advised when carfilzomib is used with other drugs that can increase the risk of thrombosis. Thromboprophylaxis should be considered according to local treatment protocols. Close monitoring for thromboembolic events is recommended in patients with known risk factors (e.g., prior thrombosis, hyperlipidemia, hypertension, smoking) for thromboembolism. Patients should be advised to seek immediate medical attention if they develop symptoms of thromboembolism, such as shortness of breath, chest pain, hemoptysis, or arm or leg swelling or pain.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39564/', '[1] "Product Information. Kyprolis (carfilzomib)." Onyx Pharmaceuticals Inc, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365204);
