INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39633, 'Conjugated estrogens', 'Pomalidomide', 'Major', 'Concomitant treatment with agents that can cause thrombosis such as erythropoiesis-stimulating agents, estrogens, selective estrogen receptor modulators, or C1 esterase inhibitors may potentiate the risk of venous thromboembolic events associated with the use of pomalidomide. Deep venous thrombosis (DVT) and pulmonary embolism (PE) occur in patients with multiple myeloma treated with pomalidomide.', NULL, 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving pomalidomide.', NULL, 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving pomalidomide. Prophylactic antithrombotic measures should be considered after assessing underlying risk factors such as health history and concomitant medications. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39689/', '[1] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.[2] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[3] Therapeutic Research Faculty "Natural Medicines Comprehensive Database. Available from: URL: http://www.naturaldatabase.com." ([1995-2008...]):[4] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc, Vancouver, AZ.', 1767365204);
