INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39642, 'Conjugated estrogens', 'Raloxifene', 'Moderate', 'The concurrent use of raloxifene and systemic estrogen or hormone replacement therapy has not been evaluated in prospective clinical trials. Safety and efficacy of this combination are unknown.', NULL, 'Due to the lack of clinical data, use of raloxifene with systemic estrogens is not recommended.', NULL, 'Due to the lack of clinical data, use of raloxifene with systemic estrogens is not recommended.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39698/', '[1] "Product Information. Evista (raloxifene)." Lilly, Eli and Company, Indianapolis, IN.[2] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[3] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767365204);
