INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39648, 'Conjugated estrogens', 'Ropinirole', 'Moderate', 'Coadministration with estrogen-containing drugs may increase the plasma concentrations of ropinirole. Data from a population pharmacokinetic study revealed that estrogens, mainly ethinyl estradiol at 0.6 to 3 mg over a 4-month to 23-year period, reduced the oral clearance of ropinirole by up to 36% in 16 patients. The mechanism of interaction has not been described.', NULL, 'Dosage adjustment for ropinirole should not be necessary in patients already on estrogen therapy because patients must be carefully titrated to tolerance or adequate effect.', NULL, 'Dosage adjustment for ropinirole should not be necessary in patients already on estrogen therapy because patients must be carefully titrated to tolerance or adequate effect. However, if estrogen therapy is initiated or discontinued during treatment with ropinirole, then patients should be monitored for altered ropinirole effects and the dosage adjusted as necessary. Patients should be advised to notify their physician if they experience agitation, orthostasis, sedation, confusion, hallucinations, and/or increased dyskinesia, flushing, dry mouth, sweating, or heart rate.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39704/', '[1] "Product Information. Requip (ropinirole)." SmithKline Beecham, Philadelphia, PA.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "