INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39650, 'Conjugated estrogens', 'Rufinamide', 'Moderate', 'Rufinamide may modestly or moderately decrease the plasma concentrations and therapeutic effects of drugs that are CYP450 3A4 substrates. The mechanism is enhanced metabolism by CYP450 3A4 enzyme induction.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose. Dose adjustments of the coadministered drug may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39706/', '[1] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[4] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[5] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39651, 'Conjugated estrogens', 'Saquinavir', 'Moderate', 'Coadministration with saquinavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by saquinavir.', NULL, 'Caution is advised if saquinavir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if saquinavir must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever saquinavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39707/', '[1] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Fortovase (saquinavir)" Roche Laboratories, Nutley, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Kupferschmidt HHT, Fattinger KE, Ha HR, Follath F, Krahenbuhl S "Grapefruit juice enhances the bioavailability of the HIV protease inhibitor saquinavir in man." Br J Clin Pharmacol 45 (1998): 355-9[7] Eagling VA, Profit L, Back DJ "Inhibition of the CYP3A4-mediated metabolism and P-glycoprotein-mediated transport of the HIV-I protease inhibitor saquinavir by grapefruit juice components." Br J Clin Pharmacol 48 (1999): 543-52[8] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.', 1767365204);
