INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39699, 'Bumetanide', 'Phenobarbital', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39755/', '[1] Feder R "Bradycardia and syncope induced by fluoxetine." J Clin Psychiatry 52 (1991): 139[2] Ellison JM, Milofsky JE, Ely E "Fluoxetine-induced bradycardia and syncope in two patients." J Clin Psychiatry 51 (1990): 385-6[3] Sternbach H "Fluoxetine-associated potentiation of calcium-channel blockers." J Clin Psychopharmacol 11 (1991): 390-1[4] Pacher P, Kecskemeti V "Cardiovascular side effects of new antidepressants and antipsychotics: new drugs, old concerns?" Curr Pharm Des 10 (2004): 2463-75[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] Andrews C, Pinner G "Postural hypotension induced by paroxetine." BMJ 316 (1998): 595[7] Rodriguez de la Torre B, Dreher J, Malevany I, et al. "Serum levels and cardiovascular effects of tricyclic antidepressants and selective serotonin reuptake inhibitors in depressed patients." Ther Drug Monit 23 (2001): 435-40[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[10] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[11] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39700, 'Buprenorphine', 'Phenobarbital', 'Major', 'Concomitant use of buprenorphine with other central nervous system (CNS) depressants has been associated with increased risk of buprenorphine overdose, severe respiratory depression, coma, and death. Coadministration with potent inducers of CYP450 3A4, including certain anticonvulsants such as carbamazepine, phenobarbital, phenytoin and primidone, may decrease the plasma concentrations of buprenorphine, which is primarily metabolized by the isoenzyme. Reduced efficacy or withdrawal symptoms may occur in patients maintained on buprenorphine.', NULL, 'Extreme caution is advised when prescribing buprenorphine in conjunction with other CNS depressants, or when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol.', NULL, 'Extreme caution is advised when prescribing buprenorphine in conjunction with other CNS depressants, or when prescribing buprenorphine to patients who are addicted to opioids and also abusing benzodiazepines or alcohol. Due to potential risk of overdose and death, dependence on sedative-hypnotics such as benzodiazepines or alcohol is considered a relative contraindication for office-based buprenorphine treatment of opioid addiction. Dosage reduction of CNS depressants, buprenorphine, or both should be considered if concomitant use is necessary. Pharmacologic response to buprenorphine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buprenorphine dosage adjusted as necessary.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39756/', '[1] "Product Information. Suboxone (buprenorphine-naloxone)." Reckitt and Colman Pharmaceuticals Inc, Richmond, VA.[2] Substance Abuse and Mental Health Services Administration (US) "Clinical Guidelines for the Use of Buprenorphine in the Treatment of Opioid Addiction. Treatment Improvement Protocol (TIP) Series, No. 40 Available from: URL: http://www.ncbi.nlm.nih.gov/books/NBK64245/" ([2004]):', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39701, 'Buspirone', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations and pharmacologic effects of buspirone, which is primarily metabolized by the isoenzyme. Conversely, discontinuation of an inducer may increase buspirone plasma concentrations and increase the risk of central nervous system depression and other adverse effects.', NULL, 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with potent CYP450 3A4 inducers.', NULL, 'The potential for diminished pharmacologic effects of buspirone should be considered during coadministration with potent CYP450 3A4 inducers. Pharmacologic response to buspirone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the buspirone dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/39757/', '[1] "Product Information. Buspar (buspirone)." Bristol-Myers Squibb, Princeton, NJ.[2] Kivisto KT, Lamberg TS, Neuvonen PJ "Interactions of buspirone with itraconazole and rifampicin: Effects on the pharmacokinetics of the active 1-(2-pyrimidinyl)-piperazine metabolite of buspirone." Pharmacol Toxicol 84 (1999): 94-7[3] Lamberg TS, Kivisto KT, Neuvonen PJ "Concentrations and effects of buspirone are considerably reduced by rifampicin." Br J Clin Pharmacol 45 (1998): 381-5[4] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] Lilja JJ, Kivisto KT, Backman JT, Lamberg TS, Neuvonen PJ "Grapefruit juice substantial