INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39964, 'Tolvaptan', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tolvaptan, which is primarily metabolized by the isoenzyme. When tolvaptan was administered with the potent CYP450 3A4 inducer rifampin, tolvaptan peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by approximately 85%.', NULL, 'Concomitant use of tolvaptan with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of tolvaptan with potent CYP450 3A4 inducers should generally be avoided. If coadministration is necessary, a dosage increase of tolvaptan may be required. Patient response should be closely monitored and the dosage adjusted accordingly.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40020/', '[1] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[4] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39965, 'Topiramate', 'Scopolamine', 'Major', 'Certain drugs such as carbonic anhydrase inhibitors and drugs with anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, disopyramide) may potentiate the risk of oligohidrosis and hyperthermia associated occasionally with the use of topiramate, particularly in pediatric patients. These agents may alter electrolyte and fluid balance (carbonic anhydrase inhibition), inhibit peripheral sweating mechanisms (anticholinergic effect), and/or interfere with core body temperature regulation in the hypothalamus (neuroleptics and phenothiazines), resulting in the inability to adjust to temperature changes, especially in hot weather. Also, agents with anticholinergic activity frequently cause drowsiness and other central nervous system-depressant effects, which may be additively or synergistically increased in patients also treated with topiramate.', NULL, 'Caution is advised when topiramate is prescribed with other drugs that predispose patients to heat-related disorders, including carbonic anhydrase inhibitors and drugs with anticholinergic activity.', NULL, 'Caution is advised when topiramate is prescribed with other drugs that predispose patients to heat-related disorders, including carbonic anhydrase inhibitors and drugs with anticholinergic activity. Patients, particularly pediatric patients, should be monitored closely for evidence of decreased sweating and increased body temperature, especially in warm or hot weather. Proper hydration before and during vigorous activities or exposure to warm temperatures is recommended. Patients (or their guardians or caregivers) should contact their physician immediately if they are not sweating as usual, with or without a fever. Ambulatory patients treated with topiramate and agents with anticholinergic activity should also be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40021/', '[1] "Product Information. Topamax (topiramate)." Ortho Pharmaceutical Corporation, Raritan, NJ.[2] "Product Information. Ionamin (phentermine)." Rhone-Poulenc Rorer, Collegeville, PA.[3] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[4] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39966, 'Toremifene', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of toremifene.', NULL, 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with toremifene. If coadministration is required, a doubling of the toremifene dosage may be necessary depending on patient tolerability. Close monitoring for toxicities (e.g., QT prolongation, hepatotoxicity, hypercalcemia and tumor flare) is recommended if the dosage of toremifene is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40022/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Berthou F, Dreano Y, Belloc C, Kangas L, Gautier JC, Beaune P "Involvement of cytochrome P450 3A enzyme family in the major metabolic pathways of toremifene in human liver microsomes." Biochem Pharmacol 47 (1994): 1883-95[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Fareston (toremifene)." Schering Laboratories, Kenilworth, NJ.[5] Kivisto KT, Villikka K, Nyman L, Anttila M, Neuvonen PJ "Tamoxifen and toremifene concentrations in plasma are greatly decreased by rifampin." Clin Pharmacol Ther 64 (1998): 648-54[6] "Product Information. F