INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (39995, 'Zolpidem', 'Phenobarbital', 'Moderate', 'Coadministration with CYP450 inducers may decrease the plasma concentrations of zolpidem, which is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2.', NULL, 'The potential for diminished pharmacologic effects of zolpidem should be considered during coadministration with CYP450 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'The potential for diminished pharmacologic effects of zolpidem should be considered during coadministration with CYP450 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Alternative treatments or a dosage adjustment for zolpidem may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/40051/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Villikka K, Kivisto KT, Luurila H, Neuvonen PJ "Rifampin reduces plasma concentrations and effects of zolpidem." Clin Pharmacol Ther 62 (1997): 629-34[3] Vlase L, Popa A, Neag M, Muntean D, Baldea I, Leucuta SE "Pharmacokinetic Interaction Between Zolpidem and Carbamazepine in Healthy Volunteers." J Clin Pharmacol (2010):[4] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[5] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[6] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bri