INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347729, 'Amobarbital', 'Maraviroc', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may decrease the plasma concentrations of maraviroc, which is a substrate of both.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if maraviroc is used with CYP450 3A4 and/or P-glycoprotein inducers. A dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if maraviroc is used with CYP450 3A4 and/or P-glycoprotein inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
