INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1347744, 'Amobarbital', 'Methoxyflurane', 'Moderate', 'Barbiturates may increase risk of methoxyflurane-induced nephrotoxicity. Laboratory and animal studies have demonstrated that barbiturates increase the metabolism of methoxyflurane to nephrotoxic metabolities (particularly inorganic fluoride), probably by CYP450 induction.', 'DDInter', 1767317475, 'Methoxyflurane should generally not be administered to patients receiving barbiturates concurrently and for several weeks after barbiturates have been discontinued.', 'Metabolism', 'Methoxyflurane should generally not be administered to patients receiving barbiturates concurrently and for several weeks after barbiturates have been discontinued.', NULL, NULL, 'Moderate', 'pharmacodynamic');
